css:
@-moz-document domain("devcamp.com") {
body, .main-row, .span12 > .row > .col-xs-4, .col-md-12 {
  background-color: #181818;
}
body nav{
  font-weight:bold;
}
.hand_raise_button, .guide-title, .column > .row, .navbar, .header,.span12 > .lesson-section > .guide-body p code, body > .app-footer {
  background-color:black;
  border-bottom-color: #000;
	border-top-color:black;
}
.span12 > .lesson-section > .guide-body > .CodeRay > .code pre {
  color: #f5f5f5;
  background-color: #333;
}
.span12 > .lesson-section > .guide-body h4 {
  color:#D8D6C2;
}
.span12 > .lesson-section, .span12 > .row > .lesson-section {
  background-color: black;
  border-color: #1a1a1a;
}
.span12 > .row {
  border:0;
}
  .bottega-copyright > .hidden-xs{
  color:#D1D0CF;
}
::-webkit-scrollbar {
  width:15px;
  background-color: #252525;
}
::-webkit-scrollbar-thumb{
  background: black;
}
::-webkit-scrollbar-track{
  border-top:60px solid black;
  border-bottom:15px solid black;
}
}
