css:
@-moz-document domain("twitter.com") {
/*disable the tweet borders*/
.r-13l2t4g, .r-qklmqi, .r-5kkj8d {
    border: none;
}

/*lower padding in notifs, etc.*/
.r-1sp51qo:not(.r-mk0yit) {
    padding: 8px;
}

/*deemphasize the drop shadow on lots of boxes*/
.r-qo02w8 {
    box-shadow: rgba(110, 118, 125, 0.25) 0px 0px 5px, rgba(100, 118, 125, 0.25) 0px 0px 10px 1px;
}

/*remove separators between links on More... tab.*/
.r-109y4c4 {
    background: none;
}

/*deemphasize the leftmost menu*/
nav[role="navigation"] div.r-vw2c0b {
    font-weight: 500;
}

/*hide the useless scrollbar next to the What's happening? box*/
.DraftEditor-editorContainer, .DraftEditor-root, .public-DraftEditor-content {
    overflow: hidden;
}

/*remove border-top-left and right radius on the menu*/
div[role="menu"] {
    border-top-left-radius: 0px;
    border-top-right-radius: 0px
}
}
