css:
@-moz-document domain("www.youtube.com") {


#related.ytd-watch.style-scope, #comments.ytd-watch.style-scope, 
.ytd-toggle-button-renderer.style-scope.yt-simple-endpoint, 
#sentiment.ytd-video-primary-info-renderer.style-scope, 
#thumbnail.fade-in.ytd-moving-thumbnail-renderer.style-scope,
#footer.ytd-guide-renderer.style-scope,
.ytp-show-tiles.videowall-endscreen.ytp-player-content.html5-endscreen,
#secondary.ytd-two-column-search-results-renderer.style-scope,
#ytd-horizontal-card-list-renderer.ytd-item-section-renderer.style-scope,
#chat.ytd-watch.style-scope,
#clarify-box.ytd-watch.style-scope,
#merch-shelf.ytd-watch.style-scope,
#secondary.ytd-watch-flexy.style-scope,
#comments.ytd-watch-flexy.style-scope,
#clarify-box.ytd-watch-flexy.style-scope,
#merch-shelf.ytd-watch-flexy.style-scope,
#sponsor-button.ytd-video-owner-renderer.style-scope,
#list-container.expanded.ytd-video-secondary-info-renderer.style-scope,
.ytd-video-secondary-info-renderer.style-scope.sticky {
    display: none;
}
#primary.ytd-watch-flexy.style-scope {
    margin-right: 12px!important;
}

#columns.ytd-watch-flexy.style-scope {
    margin-left: 0px;
}


#primary.ytd-two-column-search-results-renderer.style-scope {
    padding-left: 150px;
    padding-right: 150px;
} 


#search-form.ytd-searchbox.style-scope {
    margin-left: 40px;
} 
ytd-thumbnail-overlay-playback-status-renderer {
    width: 100%;
    height: 100%;
  }

  yt-formatted-string.style-scope.ytd-thumbnail-overlay-playback-status-renderer {
    height: 100%;
    width: 100%;
    margin: 0;
    padding-top: 47px;
  }
.ytd-video-primary-info-renderer.style-scope.super-title {
    color: #000000;
}
#ticket-shelf.ytd-watch-flexy.style-scope {
    display: none;
}
#search-form.ytd-searchbox.style-scope {
    margin-left: 178px;
}

a.yt-formatted-string.style-scope.yt-simple-endpoint {
    color: #fff;
}
#logo-icon-container,
.no-transition.ytd-topbar-menu-button-renderer.style-scope,
ytd-subscribe-button-renderer[new-subscribe-color],
paper-item.ytd-guide-entry-renderer,
.ytp-hd-quality-badge.ytp-settings-button.ytp-button,
.badge-style-type-live-now.ytd-badge-supported-renderer,
#progress.ytd-thumbnail-overlay-resume-playback-renderer,
ytd-button-renderer #button.ytd-button-renderer{
  -webkit-filter: grayscale(100%); /* Safari 6.0 - 9.0 */
  filter: grayscale(100%);
}
#text.size-default.style-blue-text.ytd-button-renderer.style-scope,
.yt-view-count-renderer.style-scope.view-count,
.ytd-button-renderer.style-scope,
#text.size-default.style-default.ytd-button-renderer.style-scope,
#button.ytd-menu-renderer yt-icon.ytd-menu-renderer,
.ytd-video-secondary-info-renderer.style-scope.date,
.more-button.ytd-video-secondary-info-renderer, .less-button.ytd-video-secondary-info-renderer,
#owner-name.ytd-video-owner-renderer,
ytd-subscribe-button-renderer[new-subscribe-color] paper-button.ytd-subscribe-button-renderer[subscribed] yt-formatted-string.ytd-subscribe-button-renderer,
.deemphasize.yt-formatted-string,
#description-text.ytd-video-renderer,
#metadata-line.ytd-video-meta-block span.ytd-video-meta-block,
#metadata-line.ytd-grid-video-renderer,
#byline-container.ytd-grid-video-renderer {
    color: #fff;
}
#subscriber-count.ytd-c4-tabbed-header-renderer,
paper-tab:not(.iron-selected) > .tab-content.paper-tab,
#label-text.yt-dropdown-menu,
a.yt-simple-endpoint.style-scope.yt-formatted-string,
#byline.style-scope.ytd-grid-vide-renderer.complexstrong,
#title-annotation.ytd-shelf-renderer {
    color: #fff;
}
a.yt-simple-endpoint.yt-formatted-string {
      color:  #fff!important;
font-weight:500;
}

}

@-moz-document domain("www.youtube.com") {
/* scrubber button */
.html5-scrubber-button:hover, .ytp-scrubber-button:hover, .ytp-swatch-background-color, .ytp-swatch-background-color-secondary {
   background: #2b2b2b !important;
}
/* progress bar */
.html5-play-progress, .ytp-play-progress {
   background: #2b2b2b !important; 
}
.ytp-volume-slider-track, .ytp-volume-slider-handle:before {
   background: #2b2b2b !important;
   z-index: -117;
}
.ytp-settings-button.ytp-hd-quality-badge::after, .ytp-settings-button.ytp-4k-quality-badge::after, .ytp-settings-button.ytp-5k-quality-badge::after, .ytp-settings-button.ytp-8k-quality-badge::after, .ytp-settings-button.ytp-3d-badge::after {
   background-color: #2b2b2b !important;
}
.ytp-swatch-color {
   color: #2b2b2b !important;
}
.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox {
   background: #2b2b2b !important;
}
.ytp-chrome-controls .ytp-button.ytp-youtube-button:hover:not([aria-disabled="true"]):not([disabled]) .ytp-svg-fill-logo-tube-lozenge {
   fill: #2b2b2b !important;
}
.ytp-cued-thumbnail-overlay:hover .ytp-large-play-button-bg, .ytp-large-play-button.ytp-touch-device .ytp-large-play-button-bg {
   fill: #2b2b2b !important;
}
.resume-playback-progress-bar {
   background: #2b2b2b !important;
}
.ytp-chrome-controls .ytp-button[aria-pressed]::after {
   background-color: #2b2b2b !important;
}
.yt-uix-checkbox-on-off input-extras2-disabled[type="checkbox"]:checked + label {
   background-color: #2b2b2b !important;
}
.video-extras-sparkbar-likes-extras2-disabled {
   background-color: #2b2b2b !important;
}

ytd-searchbox[mode="legacy"] #container.ytd-searchbox, ytd-searchbox[mode="legacy-centered"] #container.ytd-searchbox {
	background-color: rgba(0,0,0,.05);
	border: none;
	box-shadow: 0 0px rgba(0,0,0,.1);
	padding: 2px 6px;
	border-radius: 2px 0 0 2px;
    outline: none !important;
} 
ytd-searchbox[mode="legacy"][has-focus] #container.ytd-searchbox, ytd-searchbox[mode="legacy-centered"][has-focus] #container.ytd-searchbox {
    border: none;
    box-shadow: 0 0px rgba(0,0,0,.1);
}

#search-icon-legacy.ytd-searchbox {
	cursor: pointer;
	width: 65px;
	border: none;
    box-shadow: 0 0px rgba(0,0,0,.08);
	background-color: var(--ytd-searchbox-legacy-button-color);
	border-radius: 0 2px 2px 0;
	margin: 0;
	--yt-icon-button-icon-width: 24px;
	--yt-icon-button-icon-height: 24px;
}

ytd-searchbox[mode="legacy"] #search-icon-legacy.ytd-searchbox:hover, ytd-searchbox[mode="legacy-centered"] #search-icon-legacy.ytd-searchbox:hover {
	border: none;
	background-color: var(--ytd-searchbox-legacy-button-hover-color);
	box-shadow: 0 0px rgba(0,0,0,.13);
}

#search-icon-legacy.ytd-searchbox yt-icon.ytd-searchbox {
	width: 20px;
	height: 20px;
	color: var(--ytd-searchbox-legacy-button-icon-color);
	opacity: 0.6;
    transition: .4s;
    animation: bounce-reverse 1500ms linear both;
}

#search-icon-legacy.ytd-searchbox:hover yt-icon.ytd-searchbox {
	opacity: 1;
    /*transform: scale(1.05,1.05);*/
    animation: bounce 1500ms linear both;
}


}
