css:
@-moz-document url-prefix("https://osu.ppy.sh") {
/* Stripes */
.beatmap-discussion-post--bng .beatmap-discussion-post__user-stripe {
    background-color: rgb(150,110,255);
}

.beatmap-discussion-post--bng_limited .beatmap-discussion-post__user-stripe {
    background-color: rgb(150,110,255);
}

.beatmap-discussion-post--nat .beatmap-discussion-post__user-stripe {
    background-color: rgb(200, 40, 50);
}

.beatmap-discussion-post__user-stripe {
    align-self: stretch;
    background-color: hsl(var(--base-hue),10%,50%);
    border-radius: 1px;
    flex: none;
    width: 2px;
}

/* Badges */
.user-group-badge--bng {
    color: rgb(150,110,255);
}

.user-group-badge--bng_limited {
    color: rgb(150,110,255);
}

.user-group-badge--nat {
    color: rgb(200, 40, 50);
}

/* Problem Stamp */
.beatmap-discussion-message-type--problem {
    color: rgb(210,90,120);
}

/* Timestamps */
.beatmapset-discussion-message>.beatmapset-discussion-message__timestamp {
    text-decoration: none;
    border-radius: 6px;
    padding: 1px 5px;
    background-color: hsl(var(--base-hue),10%,18%);
    font-weight: 600;
    font-size: 85%;
    color: hsl(var(--base-hue),40%,80%);
}

/* Base Hue */
:root {
    --base-hue: 255;
}
}
