css:
/* 
Development tool:

The purpose of this sheet is to highlight regions that JAWS allows users to navigate by. 
CAPS + Fn + F3, then select region list
*/


footer{
    border-style: solid !important;
    border-color: green !important;
    border-width: 3pt !important;
}

*[role="contentinfo"] {
    border-style: solid !important;
    border-color: green !important;
    border-width: 3pt !important;
}

footer:before {
    border-style: solid !important;
    border-color: green !important;
    border-width: 3pt !important;
    color: white !important;
    background-color: green !important;
    content:"JAWS Regions (displays 'content info'), HTML5 Sectioning Content:(tag is Footer)" !important;
    z-index: 1000;
    visibility: visible;
}
*[role="contentinfo"]:before {
    border-style: solid !important;
    border-color: green !important;
    border-width: 3pt !important;
    color: white !important;
    background-color: green !important;
    content:"JAWS Regions(displays 'content info'), HTML5 Sectioning Content: (attribute role='contentinfo', aka. footer tag)" !important;
    z-index: 1000;
    visibility: visible;
}

/*
*[role="application"] {
    border-style: solid;
    border-color: blue;
    border-width: 3pt;
}
*[role="application"]:before {
    border-style: solid;
    border-color: green;
    border-width: 3pt;
    color: white;
    background-color: blue;
    content:"ARIA application";
}

*/

*[role="search"] {
    border-style: solid;
    border-color: red;
    border-width: 3pt;
}
*[role="search"]:before {
    border-style: solid;
    border-color: red;
    border-width: 3pt;
    color: white;
    background-color: green;
    content:"JAWS Regions (displays 'search'): (attribute role='search')";
    display: block;
}
nav {
    border-style: solid;
    border-color: black;
    border-width: 3pt;
}

*[role="navigation"] {
    border-style: solid;
    border-color: black;
    border-width: 3pt;
}
nav:before {
    border-style: solid;
    border-color: white;
    border-width: 3pt;
    color: white;
    background-color: black;
    vertical-align: left;
    content:"JAWS Regions (displays 'navigation'): (tag is 'nav')";
    display: block;
}

*[role="navigation"]:before {
    border-style: solid;
    border-color: white;
    border-width: 3pt;
    color: white;
    vertical-align: left;
    background-color: black;
   	content:"JAWS Regions (displays 'navigation'): (attribute role='navigation')";
    display: block;
}
main, *[role="main"] {
    border-style: solid;
    border-color: orange;
    border-width: 3pt;
    z-index: 999 !important;
}
main:before{
    border-style: solid;
    border-color: orange;
    border-width: 3pt;
    color: white;
    background-color: orange;
    content:"JAWS Regions (displays 'main'): (tag is 'main')";
    display: block;
    z-index: 999 !important;
}

*[role="main"]:before {
    border-style: solid;
    border-color: orange;
    border-width: 3pt;
    color: white;
    background-color: orange;
   	content:"JAWS Regions (displays 'main'): (attribute role='main')";
    display: block;
    z-index: 999 !important;
}

header {
    border-style: solid !important;
    border-color: yellow !important;
    border-width: 3pt !important;
    visibility: visible !important;
}

*[role="banner"] {
    border-style: solid !important;
    border-color: yellow !important;
    border-width: 3pt !important;
    visibility: visible !important;
}
header:before{
    border-style: solid !important;
    border-color: yellow !important;
    border-width: 3pt !important;
    color: black !important;
    background-color: yellow !important;
    content:"JAWS Regions (displays banner): (tag is header)" !important;
    visibility: visible !important;
}

*[role="banner"]:before {
    border-style: solid !important;
    border-color: yellow !important;
    border-width: 3pt !important;
    color: black !important;
    background-color: yellow !important;
    content:"JAWS Regions (displays banner): (role='banner')" !important;
    visibility: visible !important;
}
aside, *[role="complementary"] {
    border-style: solid !important;
    border-color: green !important;
    border-width: 3pt !important;
}
aside:before {
    border-style: solid !important;
    border-color: green !important;
    border-width: 3pt !important;
    color: white !important;
    background-color: green !important;
    content:"JAWS Regions (tag is Aside; NVDA reads 'complementary')" !important;
}

*[role="complementary"]:before {
    border-style: solid !important;
    border-color: green !important;
    border-width: 3pt !important;
    color: white !important;
    background-color: green !important;
    content:"JAWS Regions: ARIA complementary" !important;
}
