css:
@-moz-document url-prefix("https://ewok.conservice.com/") {
body {
    background: #222;
}

td {
    background: #222 !important;
    color: #aaa !important;
    border: none !important;
}

table tbody tr td table tbody tr td a img {
    display: none;
}

/* Not Working */
#tblIssues > tbody > tr:nth-child(1) > td:nth-child(11) {
    width: 5px !important;
}

#Form1 > table > tbody > tr:nth-child(1) > td > table > tbody > tr:nth-child(2) a font {
    color: #ffffff !important;
}

#Form1 > table > tbody > tr:nth-child(1) > td > table > tbody > tr:nth-child(1) > td:nth-child(1) {
    display: none !important;
}

#tblReport > tbody > tr:nth-child(3) font {
    color: #ffffff
}

#Form1 > table > tbody > tr:nth-child(1) > td > table {
    padding-bottom: 15px;
}

#tblReport > tbody > tr:nth-child(12) > td:nth-child(1) > font > img {
    display: none;
}

/* Fix inconsistant spacing in Issue Detail */
#tblReport > tbody > tr > td {
    padding-left: 10px !important;
}


#tblReport > tbody > tr:nth-child(1) > td > div {
    padding-left: 0px !important;
}

#tblIssues > tbody > tr:nth-child(7) {
    padding-left: inherit !important;
}

#tblReport > tbody > tr > td > strong {
    color: #ffffff
}

#tblReport > tbody > tr > td > font {
    --font-size: 8 !important;
    --font-style: italic;
}

/* Fixed weird looking button */
#btnSaveIssue {
    color: #ffffff !important;
    padding-bottom: 15 !important;
}
#btnDoAction {
    color: #ffffff !important;
    padding-bottom: 15 !important;
}

#lstActions {
    --height: 334 !important;
    padding-left: 5px !important;
}

#headerBar span,
strong {
    color: #ffffff !important;
}



a > font,
#lnkBack,
a[href^="https://ewok.conservice.com/Workspace.aspx?id="],
a[onclick^="javascript:open_url(this)"],
a[href^="javascript:getIssues('"],
a[style*="color: red"],
a[onclick^="javascript:getComment("],
a[href^="javascript:noop()"] {
    color: #aaa !important;
}

textarea,
input,
select {
    color: #aaa !important;
    background: #333 !important;
    --padding: 5px !important;
}


#hdr_btnLogout {
    display: none;
}

#YellowAlertBox {
    background-color: #333;
}

input[type^="button"] {
    cursor: pointer;
}

*[style*="background-color: #efefef"],
*[style*="background-color: #F2E7C0"] {
    background-color: #333 !important;
}

*[style*="color: #0000ff"] {
    color: #aaa !important;
}

.BrownLink {
    color: #aaa !important;
}

*[style*="background-color: rgb(251, 250, 148)"] {
    background: inherit !important;
}

.DayTag {
    background: #A1253F !important;
}

.WeekTag {
    background: #AD8900 !important;
}

.MonthTag {
    background: #538753 !important;
}

*[style*="background-color:"] {
    background: #333 !important;
}

.BlackLink,
*[style*="color: #000000"] {
    color: #aaa !important;
}

*[style*="background-color: rgb(225, 225, 225)"],
*[style*="background-color: rgb(255, 255, 255)"] {
    background: inherit !important;
}

.ac-row {
    background: #333;
}

.ac-row:hover {
    background: #555;
}

.ac-pr {
    color: #aaa;
}

.vacationMsg {
    background: #555;
}

#txtId {
    font-size: 100%;
}

/* Scroll Bars */
::-webkit-scrollbar {
    width: 8px;
}

::-webkit-scrollbar-thumb {
    background: #555;
}

/* Landing Page */
a.BigLink {
    color: #aaa;
}

/* Issue Page Tabs */
#ddcolortabs #current a,
#ddcolortabs #current span,
#ddcolortabs a:hover,
#ddcolortabs a:hover span {
    background: #263746;
    color: #aaa;
}
}
