css:
@-moz-document domain("bikroy.com") {
    body {
        overflow-x: hidden;
    }

    [class^="main-section--"] {
        max-width: 1860px;
    }
    [class^="main-section--"] > div:first-child {
        display: none;
    }

    [class^="list-wrapper"] {
        margin-right: 0;
    }
    [class^="list-wrapper"] + div {
        display: none;
    }

    [class^="page-details"] {
        display: none;
    }

    [class^="ad-list-container"] {
        max-width: none;
        margin: 0;
        padding: 15px 15px 6px;
    }

    ul[class^="list"] {
        display: flex;
        flex-wrap: wrap;
        width: 100%;
        border-top: 1px solid #d4ded9;
        border-left: 1px solid #d4ded9;
    }
    ul[class^="list"] > li {
        width: 25%;
        border-top-width: 0;
        border-right: 1px solid #d4ded9;
        border-bottom: 1px solid #d4ded9;
    }

    [class^="top-ads-container"] {
        display: none;
    }

    [class^="membership-container"] {
        position: static;
    }
    [class^="membership-container"]:before {
        content: " ";
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        background-color: #fff;
    }
    [class^="membership-container"] > div {
        display: none;
    }

    [class^="lowerboard"] {
        display: none;
    }

    [class^="footer-container--"] {
        display: none;
    }

    [class^="updated-time--"] {
        width: 100%;
        margin-top: 0;
        text-align: left;
    }
  
    [class^="card-link--"] {
        display: block;
        max-height: 102px;
    }
  
    [class^="nav--"] {
        display: none;
    }
  
    [class^="pagination--"] {
        padding-bottom: 0;
    }
  
    [class^="container--"] > [class^="divider--"] {
        display: none;
    }
  
    #app-wrapper > [class^="container--"] {
        padding-top: 4px;
    }
    
    [class^="content-snippets-container--"] {
        display: none;
    }
  
    /**
     * Hide Top Ad Banner.
     */
    [class*="main-container--"] > [class*="container--"]:not([class*="search-row-container--"]):first-child {
        display: none;
    }
  
    /**
      * Hide Spotlight Slot.
      */
    [class*="spotlight-slot--"] {
      display: none;
    }
  
    /**
      * Hide Sidebar.
      */
    [class*="main-container--"] > [class*="lg-col-3--"] {
      display: none;
    }
  
    /**
      * Hide Footer Links.
      */
    [class*="internal-links-container--"] {
      display: none;
    }
  
    /**
      * Container Width.
      */
    [class*="main-container--"] > [class*="lg-col-9--"] {
      flex-basis: 100%;
      max-width: 100%;
    }
  
    /**
      * Hide Breadcrumb.
      */
    [class*="bread-crumb-wrapper--"] {
      display: none;
    }
}
