css:
@-moz-document url-prefix("https://ott.works:8443") {
body {
    background-color: black;
    background-image: none;
}

body {
    margin-left: 250px;
    margin-right: 250px;
}

.post-body.own {
    background: linear-gradient(to right, #41423c, #393934 275px);
}

.table-content {
    padding: 10px;
    background-color: #131313;
}

.content-inner {
    margin: 0;
    margin-top: 10px;
    margin-bottom: 10px;
}

.avatar-thumb {
    width: 0;
    height: 0;
    float: none;
}

.header {
    height: 90px;
    background-image: none;
    background-size: 10px 10px;
    position: relative;
    border-bottom: none;
}

.background-image {
    position: absolute;
    max-height: 460px;
    top: 0px;
    right: 0px;
    bottom: 0px;
    left: 0px;
    background-size: cover;
    opacity: 0.1;
}

.header .logo {
    overflow: hidden;
    float: left;
    position: relative;
    z-index: 1;
    height: 100%;
    opacity: 0.2;
}

subforum-entry {
    background-color: black;
}

.subforum-info .subforum-description {
    color: #FFF;
}

.content-inner tbody tr {
    background-color: #363636;
}

a {
    color: #FFF;
}

.content-inner tbody td {
    border-bottom: 1px solid #606060;
}

.thread-entry.pinned .thread-icon {
    background-color: #3d2121;
}

.subforum-entry .subforum-lastpost {
    background-color: #2a2a2a;
    color: #fff;
    max-width: 150px;
    overflow: hidden;
    text-align: right;
}

.subforum-info .subforum-viewcount {
    color: #9b9b9b;
}

.header .gradient {
    width: 100%;
    height: 100%;
    float: right;
    color: #FFF;
    background: none;
    pointer-events: none;
    position: absolute;
    z-index: 0;
}

.header .title {
    font-size: 18px;
    text-shadow: none;
    color: #FFF;
    padding-left: 32px;
}

.content-inner thead {
    background-color: #131313;
    color: white;
}

.thread-entry .thread-views {
    text-align: center;
    color: #fff;
    background-color: #000;
    width: 40px;
}

.thread-entry .thread-replies {
    text-align: center;
    width: 40px;
    color: #fff;
}

.thread-entry .thread-lastpost {
    background-color: #000;
    text-align: right;
    width: 150px;
    color: #fff;
}

.thread-entry .thread-username {
    color: #c8c8c8;
    padding-top: 4px;
}

.thread-entry td.thread-icon {
    background-color: #454545;
    padding: 6px;
}

.thread-entry .unread {
    background-color: #593939;
    border: 1px solid #ccc;
    border-radius: 2px;
    padding: 2px;
    margin: 2px;
    white-space: nowrap;
}

.thread-entry.pinned {
    background-color: #5f3232;
}

.thread-entry.pinned .thread-lastpost {
    background-color: #3d2121;
}

.thread-entry.pinned .thread-views {
    background-color: #3d2121;
}

.thread-entry td.thread-icon {
    background-color: #454545;
    padding: 6px;
}

.new-thread {
    background-color: #454545;
    color: #fff;
    padding: 5px;
    border: 1px solid #777;
    top: 10px;
    display: inline-block;
}

.pagination span:first-of-type {
    border-left-width: 1px;
    background-color: #454545;
}

.pagination a {
    color: #ffffff;
    height: 30px;
}

.pagination span {
    border: 1px solid #888;
    border-left-width: 0;
    border-right-width: 1px;
    background-color: #2e2e2e;
    padding-top: 5px;
    padding-bottom: 5px;
}

.pagination .selected {
    font-weight: bold;
    background-color: #b2b2b2;
}

.post-body {
    background: linear-gradient(to right, #373737, #353535 275px);
    flex-grow: 1;
    display: flex;
    flex-direction: column;
    min-width: 0px;
    color: #FFF;
}

.post-body .ratings {
    font-size: 11px;
    color: #c8c8c8;
    padding-left: 6px;
}

.post-body .reply-button, .post-body .edit-button {
    background-color: #181818;
    border: 1px solid #aaa;
    color: #eee;
    float: right;
    font-size: 9px;
    font-weight: 700;
    margin: 0 4px 4px;
    padding: 2px 10px;
}

.post-top {
    background-color: #333;
    line-height: 15px;
    padding: 3px;
    vertical-align: middle;
    border-bottom: 1px solid #777;
    color: #FFF;
}

.thread-head {
    background-color: #5d5d5d;
    color: white;
    font-weight: bold;
    font-size: 11pt;
    height: 30px;
    width: 100%;
}

.post-body .quote .message {
    background-color: #52606e;
    font-size: 11px;
    padding: 10px;
}

.post-body .quote .info {
    background-color: #272d32;
    padding: 4px;
}

.post-body .quote .info a {
    font-size: 12px;
    color: #e8f2fc;
    font-weight: 700;
    text-decoration: none;
}

.post-body .content a {
    color: #cedded;
    text-decoration: underline;
}

.quick-reply {
    background-color: #343434;
    padding: 15px 20%;
}

.user-pane {
    width: 230px;
    min-width: 230px;
    background: #3b3b3b none repeat scroll 0% 0%;
    position: relative;
    overflow: hidden;
}

.background-container .background- {
    background-image: none;
}

.footer {
    height: 50px;
    background-color: #131313;
    color: white;
    padding: 20px;
}

button {
    background-color: #000;
    color: #FFF;
    border: 3px solid #000;
}
}
