@-moz-document regexp("https:\\/\\/echo\.labstack\.com\\/.*") {
/* Bootstrap */
body {
  background-color: #292e31;
}
body, h1, h2, h3, h4, h5, h6, .blog, .docs {
  color: #e3e3e3;
}
a {
  color: #ccc;
}
hr {
  border-top-color: #4d5153;
}

/* Topnav */
.topnav {
  background-color: #272c30; /* rgba black 0.05 */
  border-bottom-color: #4d5153; /* rgba white 0.1 */
}
.topnav .menu a {
  color: #ccc;
}
.topnav .menu a:not(.active):hover {
  border-bottom-color: #666;
}

