/* Search box */
#search-box {
  background-color: transparent;
  border-bottom-color: #666;
  color: #ccc;
}
/* Search box: Algolia */
.algolia-autocomplete .ds-dropdown-menu:before,
.algolia-autocomplete .ds-dropdown-menu [class^=ds-dataset-]{
  background-color: #35393b;
}

.algolia-autocomplete .ds-dropdown-menu [class^=ds-dataset-]{
  border-color: #4d5153;
}
.algolia-autocomplete .ds-dropdown-menu:before {
  border-top-color: #4d5153;
  border-right-color: #4d5153;
}
.algolia-autocomplete .algolia-docsearch-suggestion {
  background-color: transparent;
}
.algolia-autocomplete .algolia-docsearch-suggestion--category-header {
  border-bottom-color: #4d5153;
}
.algolia-autocomplete .algolia-docsearch-suggestion--subcategory-column {
  color: #737373;
}
.algolia-autocomplete .algolia-docsearch-suggestion--content:before {
  background-color: #4d5153;
}
.algolia-autocomplete .algolia-docsearch-suggestion--title {
  color: #ccc;
}
.algolia-autocomplete .algolia-docsearch-suggestion--text {
  color: #999;
}
.algolia-autocomplete .algolia-docsearch-suggestion--highlight {
  color: #8da0b6;
}

/* Breadcrumb */
ul.breadcrumb {
  background-color: #23241e;
}
ul.breadcrumb li+li::before {
  color: #4d5153;
}

/* General */
.w3-codespan {
  background-color: #1c1c1c;
  color: #dcba14;
}
.w3-bordered tr, .w3-table-all tr {
  border-bottom-color: #4d5153;
}
.w3-hover-light-gray:hover, .w3-hover-light-grey:hover, .w3-light-gray, .w3-light-grey {
  background-color: #1c1c1c !important;
  color: #ccc !important;
}


/* Sidenav */
.sidenav2 {
  background-color: #272c30; /* rgba black 0.05 */
  border-right-color: #4d5153;
}
.sidenav2 a {
  color: #6b8cae;
}
.sidenav2 a.active, .sidenav a:hover {
  color: #6dcaf6;
}
}
