css:
@-moz-document domain("mynoise.net") {
/*Dark Scrollbar*/
::-webkit-scrollbar { width: 10px; }
::-webkit-scrollbar-track { background: #fff0; }
::-webkit-scrollbar-thumb { background: #222; }
::-webkit-scrollbar-thumb:hover { background: #252525; }

.player.testimonial,
.player,
.content,
.fancy,
.contentShrunk,
.contentText,
.menuNew
{
    box-shadow: none;
    border: none;
}

.menuNav
{
    bottom: 0;
    border-radius: 0;
}

.allContent {
    width: 50vw;
    min-width: 0px;
}

.player 
{
  width: 100%;
}
}

@-moz-document domain("rain.today") {
/*Dark Scrollbar*/
::-webkit-scrollbar { width: 10px; }
::-webkit-scrollbar-track { background: #fff0; }
::-webkit-scrollbar-thumb { background: #222; }
::-webkit-scrollbar-thumb:hover { background: #252525; }

html, body
{
    background-color: #333;
}

#paper
{
    background-color: #444 !important;
}

#controlPanel > .colorBg
{
    background-color: #000 !important;
    filter: sepia(100%) saturate(32%) hue-rotate(130deg) brightness(88%) contrast(80%) invert(1);
}

#noiseLayer,
#bgPictureLayer0, #bgPictureLayer1
{
    border-color: #222 !important;
}

#title
{
    background-color: #222 !important;        
}
}

@-moz-document domain("calmyleon.com") {
/*Dark Scrollbar*/
::-webkit-scrollbar { width: 10px; }
::-webkit-scrollbar-track { background: #fff0; }
::-webkit-scrollbar-thumb { background: #222; }
::-webkit-scrollbar-thumb:hover { background: #252525; }

html, body,
.colorBg
{
  background-color: #333 !important;
}

.invert
{
  filter: brightness(.1);
}

.invert,
.section
{
  color: #ccc;
}

.Lcol
{
  color: #515151 !important;

  font-weight: 1000;
  text-shadow: 0px 0px 5px #000, 0px 0px 5px #000, 0px 0px 5px #000;
  filter: brightness(1);
}

.main.dynamic.selected
{
  border-width: 5px;
}
}

@-moz-document domain("noises.online") {
/*Dark Scrollbar*/
::-webkit-scrollbar { width: 10px; }
::-webkit-scrollbar-track { background: #fff0; }
::-webkit-scrollbar-thumb { background: #222; }
::-webkit-scrollbar-thumb:hover { background: #252525; }

html, body,
.overlayNav
{
    background: #333 !important;
}

blockquote
{
    background-color: #444;
}

body,
#msg,
p, h1,
table.presets td,

#title
{
    color: #ccc;
}

blockquote,
.navLink
{
    color: #999;
}

.ctrlImg,
.lvlImg
{
    filter: invert(1) brightness(100);
    border-color: #000;
}

html
{
  overflow-x: hidden;
}
}

@-moz-document domain("purrli.com") {
/*Dark Scrollbar*/
::-webkit-scrollbar { width: 10px; }
::-webkit-scrollbar-track { background: #241533; }
::-webkit-scrollbar-thumb { background: #49276b; }
::-webkit-scrollbar-thumb:hover { background: #252525; }

.tile2
{
  width: 60%;
  margin: 0 auto;
}
}

@-moz-document domain("brainaural.com") {
/*Dark Scrollbar*/
::-webkit-scrollbar { width: 10px; }
::-webkit-scrollbar-track { background: #fff0; }
::-webkit-scrollbar-thumb { background: #222; }
::-webkit-scrollbar-thumb:hover { background: #252525; }

html
{
  background: #000c
}

.tile1
{
  background: #000000a1
}

.group
{
  background: #00000038;
  border: none !important;
}

#title .black, #subtitle .black
{
  text-shadow: none;
  color: #ccc;
}

.ui-slider-horizontal
{
  background: #ccc;
}

.section
{
  background: #373737;
  color: #ccc;
}

#msg,
input[type="text"], .label
{
  text-shadow: none;
  color: #ccc;
}
}
