@-moz-document url("https://tweetdeck.twitter.com/") {
body, #container
  {
    font-family: 'Segoe UI', 'Arial', 'Helvetica', sans-serif;
    text-shadow: none;
  }

  .column
  {
    box-shadow: none !important;
    border-right: 1px solid #444;
    margin: 0;
  }

  h1, h1 *, .attribution
  {
    color: #fff;
  }

.column-type-icon
  {
    color: #fff !important;
  }

  .column-title,.column-title .attribution
  {
    color: #fff !important;
  }

  .column-header-link i
  {
    color: #fff;
  }

  .column-header-link i:hover
  {
    color: #444;
  }

  .column-number
  {
    color: #fff !important;
  }

.link-complex:active .link-complex-target, .link-complex:focus .link-complex-target, .link-complex:hover .link-complex-target, .link-complex:visited .link-complex-target {
    text-decoration: none !important;
}

html.dark .link-complex:active .link-complex-target, html.dark .link-complex:focus .link-complex-target, html.dark .link-complex:hover .link-complex-target, html.dark .link-complex:visited .link-complex-target {
    text-decoration: none !important;
}

a:hover {
    text-decoration: none !important;
}

/* 1ST COLUMN */

  section:nth-child(1) .column-header
  {
    background: #b90600 !important;
  }

  .column-nav-item:nth-child(1) .column-nav-link .icon
