css:
@-moz-document regexp("http?.*\/\/.*:.*\/gui\/")
{
* {
    margin: 0;
    padding: 0;
}
* {
    scrollbar-color: #2a2c2e #1c1e1f;
}
body {
    background-color: rgb(34, 36, 37);
    border-color: currentcolor;
}
html, body, input, textarea, select, button {
    color: rgb(232, 230, 227);
}
html, body, input, textarea, select, button {
    background-color: #181a1b;
}
textarea {
    border-color: rgb(60, 60, 60);
}
.disabled input, .disabled select, input.disabled, select.disabled {
    background-color: rgb(26, 27, 28);
    color: rgb(186, 181, 171);
}
a {
    color: rgb(195, 190, 181);
    text-decoration-color: currentcolor;
}
#modalbg, #overlay {
    background-color: rgb(24, 26, 27);
    background-image: none;
}
#overlay .msg {
    background-color: rgb(34, 36, 37);
    border: 1px solid rgb(60, 60, 60);
}

/* TOOLBAR */
#mainToolbar {
    background-color: rgb(29, 31, 32);
    background-image: none;
    border-bottom-color: rgb(68, 68, 68);
}
#mainToolbar .separator {
    background-color: rgb(51, 54, 57);
    border-color: currentcolor;
}

/* SIDEBAR */
#mainCatList {
    background-color: rgb(24, 26, 27);
    border-color: rgb(75, 75, 75);
}
#mainCatList li.sel {
    background-color: rgb(42, 45, 47);
    color: rgb(255, 255, 255);
}
#mainCatList hr {
    background-color: rgb(59, 62, 65);
    border-color: currentcolor;
}

/* TOP RIGHT BODY */
#mainTorList tr:nth-child(odd) {
    background: #202020;
}
.stable-head, input.btn {
    background-color: rgb(42, 45, 47);
    background-image: none;
}
input.btn {
    border-color: rgb(70, 70, 70);
}
.stable-head td:hover {
    border-bottom-color: rgb(75, 75, 75);
    background-color: rgb(24, 26, 27);
}
.stable-body {
    background-color: rgb(24, 26, 27);
    background-image: none;
}
.stable-body tr.selected {
    background-color: rgb(42, 45, 47) !important;
    color: rgb(255, 255, 255) !important;
}
.stable-move-header {
    background-color: rgb(24, 26, 27);
    border-color: rgb(75, 75, 75);
}
.stable-separator-header {
    background-color: rgb(51, 54, 57);
    background-image: none;
}
table {
    border-color: #4c4c4c;
}
.stable-head td {
    border-color: rgb(51, 51, 51) rgb(60, 60, 60) rgb(60, 60, 60) rgb(51, 51, 51);
}
.stable {
    background-color: rgb(24, 26, 27);
    background-image: none;
    border-color: rgb(75, 75, 75);
}
.stable-progress {
    background-color: rgb(24, 26, 27);
    background-image: none;
    border-color: rgb(67, 67, 67);
    color: rgb(232, 230, 227) !important;
}
.stable-progress-bar {
    background-color: rgb(14, 78, 157);
    background-image: none;
    color: rgb(255, 255, 255) !important;
}

/* BOTTOM RIGHT BODY */
.folder-tabs .selected > a {
    background-color: rgb(24, 26, 27) !important;
    border-color: rgb(0, 122, 204) rgb(75, 75, 75) rgb(51, 51, 51);
    color: rgb(232, 230, 227);
}
.folder-tabs li > a {
    background-color: rgb(34, 36, 37);
    border-color: rgb(67, 67, 67) rgb(67, 67, 67) rgb(75, 75, 75);
    color: rgb(211, 208, 201);
}
.folder-tabs li > a:hover {
    background-color: rgb(32, 34, 35);
    border-top-color: rgb(0, 122, 204);
}
#mainInfoPane-content {
    background-color: rgb(24, 26, 27);
    background-image: none;
    border-color: rgb(75, 75, 75);
}
#mainInfoPane-generalTab, #mainInfoPane-loggerTab {
    background-color: rgb(24, 26, 27);
    border-color: rgb(75, 75, 75);
}
#mainInfoPane-generalTab td.header {
    background-color: rgb(27, 28, 30);
    background-image: none;
    border-color: rgb(60, 60, 60);
}
#mainInfoPane-speedTab {
    background-color: rgb(24, 26, 27);
}
.stable-infobar {
    background-color: rgb(24, 26, 27);
    background-image: none;
    border-top-color: rgb(75, 75, 75);
}

/* BOTTOM RIGHT BODY STATUSBAR*/
#mainStatusBar {
    background-color: rgb(29, 31, 32);
    background-image: none;
    border-top-color: rgb(68, 68, 68);
}
#mainStatusBar .section {
    border-left-color: rgb(61, 61, 61);
}

/* DIALOG */
.dlg-window {
    background-color: rgb(24, 26, 27);
    border-top-color: rgb(64, 64, 64);
    border-left-color: rgb(64, 64, 64);
    border-bottom-color: rgb(70, 70, 70) !important;
    border-right-color: rgb(70, 70, 70) !important;
    box-shadow: rgba(0, 0, 0, 0.15) 3px 3px 3px, rgba(0, 0, 0, 0.1) 0px 0px 6px;
    color: rgb(232, 230, 227);
}
.dlg-top .dlg-head {
    background-color: rgb(33, 35, 37);
    border-color: rgb(72, 72, 72);
    color: rgb(221, 218, 213);
}
#dlgSettings-menuCont {
    background-color: rgb(24, 26, 27);
    border-right-color: rgb(60, 60, 60);
}
#dlgSettings-menu .selected > a {
    background-color: rgb(0, 122, 204);
    color: rgb(255, 255, 255);
}
input.fbox, input.tbox, select {
    background-color: rgb(24, 26, 27);
    border-color: rgb(60, 60, 60);
    color: rgb(232, 230, 227);
}
fieldset {
    border-color: rgb(60, 60, 60);
}
fieldset legend {
    color: rgb(232, 230, 227);
}
.dlg-foot {
    background-color: rgb(28, 30, 31);
    background-image: none;
    border-top-color: rgb(60, 60, 60);
}
#dlgSettings-CookieManager {
 	overflow: hidden !important;   
}

/* CONTEXT MENU */
.CMenu {
    background-color: rgb(26, 28, 29);
    background-image: none;
    border-color: rgb(68, 67, 71);
    box-shadow: rgba(0, 0, 0, 0.3) 2px 2px 2px, rgba(0, 0, 0, 0.2) 0px 0px 4px;
}
.CMenu ul {
    background-color: rgb(26, 28, 29);
}
.CMenu a {
    color: rgb(195, 190, 181);
}
.CMenu a.dis {
    background-color: rgb(26, 28, 29) !important;
/*     color: rgb(198, 194, 186) !important; */
        color: rgb(110, 106, 98) !important;
}
.CMenu ul li.sep {
    border-top-color: rgb(60, 60, 60);
}
}
