css:
@-moz-document domain("duckduckgo.com") {
.onboarding-ed__arrow-teaser .onboarding-ed__arrow-teaser__alpinist {
    height: 0px;
    width: 0px;
    opacity: 0.85;
    transition: opacity .25s ease;
}
.onboarding-ed__arrow-teaser .onboarding-ed__arrow-teaser__arrow-wrapper .onboarding-ed__arrow-teaser__arrow {
    height: 0px;
    width: 0px;
    top: 50%;
    left: 50%;
    margin: -8px;
    position: absolute;
}
.onboarding-ed__arrow-teaser .onboarding-ed__arrow-teaser__arrow-wrapper {
    background: rgba(34, 34, 34, 0);
    background-size: 24px;
    border-radius: 100%;
    height: 32px;
    width: 32px;
    bottom: -3px;
    left: 50%;
    margin: -16px;
    position: absolute;
}
.search, .search--adv {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    border-radius: 4px;
    display: block;
    position: relative;
    height: 2.8em;
    background-image: -webkit-linear-gradient(top,#FFFFFF,#f1f1f1);
    border: 1px solid rgba(0,0,0,0.15);
    box-shadow: 0 2px 3px rgba(0,0,0,0.06);
    padding-left: .75em;
    padding-right: 6.5em;
}
.search__button:hover, .search__button:focus, .search:hover .search__button:focus, .search--header.has-text.search--hover .search__button:hover, .search--header.has-text.search--focus .search__button:hover, .search--home.has-text .search__button:focus, .search--home.has-text .search__button:hover {
    color: white;
    background-image: -webkit-linear-gradient(top,#9FDA53,#568E24);
}
.search__button, .search__button--hero {
    border-radius: 0 4px 4px 0;
    min-width: 26px;
    background-image: -webkit-linear-gradient(top,#568E24,#6E963A);
    color: #F7F7F7;
    font-size: 1.25em;
    padding: 0 .64em;
    height: auto;
    min-height: 1.8em;
    margin-top: -1px;
    margin-bottom: -1px;
    margin-right: -3px;
    line-height: 1.5;
    background-color: transparent;
    background-position: 50% 50%;
    background-repeat: no-repeat;
    -webkit-font-smoothing: subpixel-antialiased;
}
.logo_homepage {
    width: 250px;
    height: 200px;
    display: block;
    position: relative;
    margin: 0 auto;
    background-image: url("https://web.archive.org/web/20140831161658im_/https://www.google.com/images/srpr/logo9w.png");
}
.body--home, .site-wrapper--home {
    background: #FFFFFF;
}
.logo_homepage__tt {
    padding: 0 12px;
    font-weight: 600;
    font-size: 16px;
    bottom: auto;
    right: auto;
    left: 105%;
    top: 35%;
}
.logo_homepage__tt {
    -webkit-transition: opacity .3s ease-in-out;
    -webkit-transition-delay: 0s;
    -moz-transition: opacity .3s ease-in-out 0s;
    -o-transition: opacity .3s ease-in-out 0s;
    transition: opacity .3s ease-in-out 0s;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    -ms-border-radius: 4px;
    -o-border-radius: 4px;
    border-radius: 2px;
    visibility: hidden;
    opacity: 0;
    display: inline-block;
    vertical-align: middle;
    position: absolute;
    margin: auto;
    background-color: #a3a3a3;
    background-color: rgba(16, 16, 16, 0.74);
    text-indent: 0px;
    padding: 0 1em;
    white-space: nowrap;
    line-height: 1.6;
    height: 1.6em;
    font-weight: 400;
    font-style: normal;
    color: white;
    z-index: 200;
}
.header__logo, .header__praise, .header__avatar {
    width: 80px;
    height: 30px;
}
.header__logo {
    background-size: 80px 28px;
    background-image: url("https://web.archive.org/web/20140831161658im_/https://www.google.com/images/srpr/logo9w.png");
}
.header-wrap {
    -webkit-box-shadow: 0 1px 0 rgba(0,0,0,0.15);
    -moz-box-shadow: 0 1px 0 rgba(0,0,0,0.15);
    box-shadow: 0 1px 0 rgba(0,0,0,0.15);
    -webkit-tab-highlight-color: rgba(0,0,0,0);
    background-color: #FFFFFF;
    padding-top: 16px;
    position: relative;
    z-index: 15;
}
.tile, .tile--s, .tile--info {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    border-radius: 1px;
    overflow: hidden;
    display: block;
    position: relative;
    background-color: #fff;
    border: 1px solid #e3e3e3;
    border-bottom-color: #cacaca;
    font-size: .87em;
    margin: 8px 5px;
    line-height: 1.37;
    padding: 0;
    opacity: 1;
    box-shadow: 0 2px 3px rgba(0,0,0,0.06);
    border-bottom-color: rgba(125,125,125,0.3);
}
.detail--xd .c-detail__btn {
    border-color: rgba(0,0,0,0.75);
    color: #aaa;
    background-image: -webkit-linear-gradient(top,#2D2D2D,#151515);
}
.detail--xd {
    -webkit-transition: background-color .3s ease-in-out;
    -webkit-transition-delay: 0s;
    -moz-transition: background-color .3s ease-in-out 0s;
    -o-transition: background-color .3s ease-in-out 0s;
    transition: background-color .3s ease-in-out 0s;
    background-image: -webkit-linear-gradient(top,#080808,#353535);
}
.tile-nav, .tile-nav--sm {
    -webkit-transform: translate3d(0, 0, 140px);
    -moz-transform: translate3d(0, 0, 140px);
    -ms-transform: translate3d(0, 0, 140px);
    -o-transform: translate3d(0, 0, 140px);
    transform: translate3d(0, 0, 140px);
    -webkit-border-radius: 3%;
    -moz-border-radius: 50%;
    -ms-border-radius: 50%;
    -o-border-radius: 50%;
    border-radius: 8%;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    -webkit-transition: all .3s ease-in-out;
    -webkit-transition-delay: 0s;
    -moz-transition: all .3s ease-in-out 0s;
    -o-transition: all .3s ease-in-out 0s;
    transition: all .3s ease-in-out 0s;
    font-style: normal;
    -webkit-user-select: none;
    -khtml-user-select: none;
    -moz-user-select: -moz-none;
    -ms-user-select: none;
    user-select: none;
}
.detail--xd .tile-nav--sm {
    background-color: #212121;
    color: #EFEFEF;
}
}
