css:
@-moz-document domain("kan-tv.com") {

body,
.sitemap {
    background-color: #38383d;
}
.sitemap > a {
    color: #b1b1b3 !important;
}
.resource,
#page .layui-box a,
#page .layui-box span,
.article-detail,
.article-category,
.child-nav {
    background-color: #ccc;
}
.article-category-list a,
.child-nav span {
    border-color: #000 !important;
}
.article-category-list a:hover,
.child-nav span:hover {
    color: #009933 !important;
    border-color: #009933 !important;
}

.category-toggle {
    bottom: 10px;
    top: unset;
}
blockquote.shadow > div, blockquote.shadow > div > span {
    color: #b1b1b3 !important;
}
}
