@-moz-document domain("twitter.com") {
/*
 * ------------------------------Background related stuff-------------------------------------------------------------       
 */

.enhanced-mini-profile, .modal-tweet-form-container, .ThreadedConversation-showMore, .module .active .list-link, #global-tweet-dialog .modal-tweet-form-container, html[dir="ltr"], body.nofooter, html[xmlns="http://www.w3.org/1999/xhtml"], .AdaptiveSearchPage .AppContent, [data-platform="web"], .route-download #page-outer, .fast-signup.logged-out, .route-profile #page-outer .AppContent#doc.route-connect, .MomentsPage, .route-account_suspended, .AdaptiveDefaultPage #doc, .Streams #timeline, .overlay-enabled, .PermalinkProfile-background.without-banner, .tfw .footer, .tfw.noloki
  {
    background: none;
  }

  element.style
  {
    background-color: rgb(25, 25, 25);
  }

  body
  {
    background-color: rgb(25, 25, 25)!important;
    background-attachment: fixed;
    background-position: center 46px!important;
    background-repeat: no-repeat;
    background-image: url(/*[[background]]*/);
  }
/*
 * --------------------------------------------------------------------------------------------------------------------
 */
/*
 *--------------------------Font--------------------
 */
  .r-1b6yd1w
  {
    font-size: 16px;
  }
/*
 *--------------------------Twitter Logo--------------------
 */
  .r-mtrfb5
  {
    min-width: 40px;
  }
  .r-1vvnge1{
    margin-left: 120px;
}

  .r-1jayybb
  {
    min-height: 40px;
  }
/*
 *--------------------------TL Content--------------------
 */
  .r-a023e6
  {
    font-size: 14px;
  }

  .r-1uaug3w
  {
    background-color: rgb(32,32,32);
  }
/*
 *--------------------------Right Sidebar--------------------
 */
  .r-1uhd6vh
  {
    background-color: rgb(25, 25, 25);
    max-width: 350px;
    padding-right: 10px;
    border-bottom-color: rgb(22, 22, 22);
    border-left-color: rgb(22, 22, 22);
    border-right-color: rgb(22, 22, 22);
    border-top-color: rgb(22, 22, 22);
  }

  .r-yfoy6g
  {
    background-color: rgb(25, 25, 25);
  }

  .r-1joea0r
  {
    margin-left: 2px;
  }

  .r-1ovo9ad
  {
    width: 350px;
    margin-right: 50px;
    ;
  }

  .r-1l5qxre
  {
    padding-bottom: 0px;
    background-color: rgb(25,25,25,0.7);
    padding-left: 5px;
    padding-right: 5px;
    border-bottom-left-radius: 14px;
    border-bottom-right-radius: 14px;
    border-top-left-radius: 14px;
    border-top-right-radius: 14px;
  }
/*
 *--------------------------Top bar--------------------
 */
  .r-1h3ijdo
  {
    height: 45px;
  }

  .r-aaos50
  {
    background-color: rgb(20, 20, 20);
  }
/*
 *--------------------------Left sidebar--------------------
 */
  .r-d0pm55
  {
    max-width: 500px;
    padding-left: 20px;
    margin-left: 16px;
    background-color: rgb(25,25,25,0.7);
    border-bottom-left-radius: 14px;
    border-bottom-right-radius: 14px;
    border-top-left-radius: 14px;
    border-top-right-radius: 14px;
}
  .r-e7q0ms
  {
    max-width: 250px;
    padding-left: 20px;
    margin-left: 16px;
  }
/*
 *--------------------------Scrollbar--------------------
 */
  ::-webkit-scrollbar
  {
    background-color: rgb(25,25,25);
  }

  ::-webkit-scrollbar-thumb
  {
    background-color: rgb(10,10,10);
  }
/*
 *--------------------------Who to follow--------------------
 */
  .r-gzqks2
  {
    min-height: 20px;
    max-height: 300px;
  }
/*
 *--------------------------Twiiter info & "X retwitted"--------------------
 */
.r-1sf4r6n {
    width: auto;
    max-width: 400px;
}
.r-13wfysu, .r-1ddef8g {
    
    max-width: 400px;
}
.r-ry3cjt {
    width: auto;
    max-width: 200px!important;
}
}
