css:
@-moz-document regexp(".*(www|old|new).reddit.com.*") {
/* Arrows and scores */
.score.unvoted,
.arrow.down,
.arrow.up,
.awardings-bar, /* Gilded */
.give-gold-button, /* Award gold button */
/* New reddit */
.voteButton, /* Arrows */
.voteButton + div, /* Trick to select score, it usually comes right after upvote */
[class$="cxkdic"], /* Comment score */
[id^="PostAwardBadges"], /* Gilded */
[data-click-id^="comments"] + button, /* Give award */
[data-test-id^="comment"] + div>button:nth-child(2), /* Comment give award */
[data-test-id^="post-content"]>div:nth-child(4)>div:first-child>button:nth-child(2) /* Post give award */
{
    display: none !important
}


.midcol.unvoted {
    /* Necessary to make width exist on an empty div (.midcol.unvoted is the class for divs that contain the arrows and score) */
    min-height: 1px !important;
    /* Sets width (i don't think it's a precise set tbh, it just reduces the size of the box generated by min-height: 1px */
    width: 1px !important
}
}
