css:
@-moz-document domain("twitter.com") {

/*timeline*/
article div:nth-child(2) div:nth-child(2) div:nth-child(3) div div div a:not([data-focusable]) div div div[style^="padding-bottom: 56"]:nth-child(1) {
    padding-bottom: /*[[height]]*/% !important;
}
article div:nth-child(1) div:nth-child(2) div:nth-child(3) div div div a:not([data-focusable]) div div div[style^="padding-bottom: 56"]:nth-child(1) {
    padding-bottom: /*[[height]]*/% !important;
}
article div:last-child div:nth-child(2) div div:nth-child(4) div div div a:not([data-focusable]) div div div[style^="padding-bottom: 56"]:nth-child(1) {
    padding-bottom: /*[[height]]*/% !important;
}

/*opened thread*/
article div div:nth-child(2) div div:nth-child(2) div div div a:not([data-focusable]) div div div[style^="padding-bottom: 56"]:nth-child(1) {
    padding-bottom: /*[[height]]*/% !important;
}
}
