css:
@-moz-document domain("www.givero.com/") {

  /*
  Givero Dark Night
	Author: Mayara Santayana (Santayana / MSantayana / May Santayana)
	Page in userstyles.org: https://userstyles.org/users/835666/
  Page in GitHub: https://github.com/MSantayana/givero-dark-night
	Version: 0.2.4
*/
  body {
    background-color: rgb(39, 43, 65);
    color: rgb(226, 217, 254) !important;
  }

  #search-header {
    background-color: rgb(56, 54, 78);
    -webkit-box-shadow: -2px 18px 37px -23px rgba(20, 20, 20, 1);
    -moz-box-shadow: -2px 18px 37px -23px rgba(20, 20, 20, 1);
    box-shadow: -2px 18px 37px -23px rgba(20, 20, 20, 1);
  }

  .search-results a, .search-config a {
    color: rgb(160, 133, 249) !important;
  }

  .search-config a:hover {
    color: rgb(139, 106, 249) !important;
    transition: 0.89s;
  }

  .search-results p {
    color: rgb(226, 217, 254) !important;
  }

  .search-results h3 a {
    color: rgb(116, 221, 226) !important;
    text-shadow: 0 0 3vw #80c6ff;
  }

  .search-results .results-top-info .num-results {
    color: rgb(226, 217, 254) !important;
  }

  #search-header .tab-modern .tab-modern__nav-link a {
    color: rgb(231, 225, 250) !important;
  }

  #search-header .tab-modern .tab-modern__nav-link a:hover {
    color: rgb(194, 235, 241);
  }

  #search-header .tab-modern .tab-modern__nav-link.active {
    color: rgba(68, 73, 190, 1);
  }

  a.blue {
    color: rgb(103, 46, 254);
  }

  .search-related .related h3 {
    color: rgb(226, 217, 254) !important;
  }

  .search-wrapper input {
    background-color: rgb(33, 28, 60);
    color: rgb(195, 250, 246);
    text-shadow: 0 0 4vw #b4ddfe;
  }

  .search-autocomplete .search-autocomplete-item:hover, .search-autocomplete .search-autocomplete-item.selected {
    background-color: rgb(15, 11, 36);
    color: rgb(150, 247, 240);
    text-shadow: 0 0 4vw #96c3e7;
  }

  .clear-button, .search-wrapper button {
    background-color: rgb(33, 28, 60) !important;
  }

  .clear-button, .search-wrapper button:hover {
    background-color: rgb(41, 36, 70) !important;
  }

  .search-results .results-top-info .search-support .next-search {
    color: rgb(150, 112, 250);
  }

  .page-item.active-kazan .page-link {
    color: white;
    background-color: rgb(56, 54, 78);
  }

  .page-link {
    background-color: rgb(39, 43, 65);
  }

  .page-link:hover {
    color: white;
    background-color: rgb(79, 64, 111);
    transition: 1s;
  }

  p {
    color: rgb(249, 194, 245);
  }

  .card, .card-header {
    background-color: rgb(88, 67, 112);
    color: rgb(249, 194, 245);
    border-color: rgb(88, 67, 112);
  }

  .text-dark {
    color: rgb(255, 241, 254) !important;
  }

  .bg-third-color {
    background-color: rgb(88, 67, 112);
    color: rgb(249, 194, 245);
    border-color: rgb(88, 67, 112);
  }

  .u-sidebar__container {
    color: rgb(226, 217, 254) !important;
    background-color: rgb(56, 54, 78);
    -webkit-box-shadow: -16px 21px 101px -23px rgba(20, 20, 20, 1);
    -moz-box-shadow: -16px 21px 101px -23px rgba(20, 20, 20, 1);
    box-shadow: -16px 21px 101px -23px rgba(20, 20, 20, 1);
  }

  .u-sidebar .inline-svg-icon {
    fill: rgb(226, 217, 254) !important;
  }

  .text-muted, .u-sidebar--panel__link {
    color: rgb(226, 217, 254) !important;
  }

  .main-color {
    background-color: rgb(88, 67, 112) !important;
    color: rgb(249, 194, 245) !important;
    border-color: rgb(88, 67, 112);
  }

  .u-unfold {
    background-color: rgb(88, 67, 112) !important;
    color: rgb(249, 194, 245) !important;
    border-color: rgb(88, 67, 112);
    -webkit-box-shadow: -2px 18px 37px -23px rgba(20, 20, 20, 1);
    -moz-box-shadow: -2px 18px 37px -23px rgba(20, 20, 20, 1);
    box-shadow: -2px 18px 37px -23px rgba(20, 20, 20, 1);
  }

  .list-group-flush .list-group-item.active {
    color: rgb(226, 217, 254) !important;
  }

  .search-autocomplete:not(:empty) {
    background-color: rgb(33, 28, 60);
    color: rgb(195, 250, 246);
    text-shadow: 0 0 4vw #b4ddfe;
  }
}
