css:
@-moz-document url-prefix("https://www.vibeboost.co/") {
main {
    background: -webkit-linear-gradient(top, #f9f9f9 12%, #f6f8f8 30%, #f3f6f7 54%, #f2f5f6 75%, #f1f3f5 100%);
}
.rainbowWrapper {
    -webkit-text-fill-color: #ff6161;
}
.center, .center-align {
    text-align: center;
}
.green-text.text-lighten-5 {
    color: #489e4e !important;
}
.green-text.text-lighten-4 {
    color: #44754d !important;
}
.btn.disabled {
    color: #8e809a !important;
}
h5 {
    font-size: 30px;
}
.btn-floating i {
    color: #ffffff;
}
.btn, .btn-large, .btn-small {
    color: #ffffff;
    font-size: 20px;
}
.deep-purple.accent-1 {
    background-color: #c5aeff !important;
}
.green-text.text-accent-1 {
    color: #0a91d4 !important;
}
.green-text.text-accent-2 {
    color: #c691ed !important;
}
h5 > span {
    display: none;
}
nav .brand-logo {
    padding: 0px;
    margin-left: 30px;
    margin-top: 30px;
    position: absolute;
    font-family:'Dosis', sans-serif;
    font-size: 4em;
    font-weight: bold;
    text-transform: lowercase;
    overflow: hidden;
    white-space: nowrap;
    background-image: none;
    display: block;
    z-index: 1;
}
.z-depth-1, nav, .card-panel, .card, .toast, .btn, .btn-large, .btn-small, .btn-floating, .dropdown-content, .collapsible, .sidenav {
    box-shadow: none;
    -webkit-box-shadow: none;
}
.brand-logo:before {
    content:"â˜‚  "
}
.deep-purple.accent-4 {
    background-color: #d3baff !important;
}
nav ul a {
    color: #f7f2ff;
    background-color: #ceb2ff;
}
.dropdown-content li:hover, .dropdown-content li.active {
    background-color: #d2b8ff;
}
.dropdown-content li>a, .dropdown-content li>span {
    color: #ffffff;
}
.row .col.s8 {
    width: 100%;
}
.card {
    animation-name: videos;
    animation-duration: 0.5s;
}
.card .card-image img {
    display: inline;
}
.vbNewLines {
    font-size: 20px;
}
ul#comsList {
    list-style-image: url("https://i.imgur.com/sicnVPB.png");
}
.btn:hover {
    background-image: -webkit-linear-gradient(120deg, #3afea5, #42cce0);
    -webkit-animation: hue 2.22s infinite linear;
    -moz-animation: hue 2.22s infinite linear;
    box-shadow: none;
    -webkit-box-shadow: none;
}
.vbTabsHeadSpacing {
    margin-bottom: 30px;
}
img {
    height: 275px;
    object-fit: cover;
}
div.card-content {
    padding: 12px;
}
.vbCardStyling2 {
    background-color: #ffffff !important;
}
.vbCardStyling1 {
    border-image: linear-gradient(120deg, #9499e9 0%, #eeaeca 100%);
    border-image-source: linear-gradient(120deg, rgb(148, 153, 233) 0%, rgb(238, 174, 202) 100%);
    border-image-slice: 1;
    border-image-width: 2.5px;
    border-image-outset: 0;
    border-image-repeat: stretch;
    border-image-slice: 1;
    border-image-width: 2.5px;
}
.tabs .tab a.active {
    color: #ffffff;
    background-color: #c5aeff !important;
}
.tabs .indicator {
    background-color: #9499e9;
}
.tabs .tab a {
    color: #aa9bfa;
    background-color: #d3baff;
}
a.cyan-text {
    text-decoration: none;
    background-image: linear-gradient(currentColor, currentColor);
    background-position: 0% 100%;
    background-repeat: no-repeat;
    background-size: 0% 2px;
    transition: background-size .3s;
}
a.cyan-text:hover {
    background-size: 100% 2px;
}
i.medium {
    font-size: 2em;
}
.vbIconStatus {
    cursor: context-menu;
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    -khtml-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}
.vbMuted.material-icons.small {
    color: white;
    border-radius: 50%;
    background: rgba(0, 0, 0, 0.21);
}
.vbPBR, #loadingContainer, #video {
    max-width: 800px;
    width: 800px;
}
@keyframes videos {
    from {
        bottom: 10px;
        opacity: 0;
    }
    to {
        bottom: 0px;
        opacity: 1;
    }
}
@font-face {
    font-family:'Dosis', sans-serif;
    src: url(https://fonts.googleapis.com/css?family=Dosis&display=swap);
}
}
