css:
@-moz-document domain("comments.app") {
body {
    color: #eee;
    background: #131313;
}

.tgme_widget_message_bubble {
    background: #101010;
    border-color: #222;
}

.tgme_widget_message_bubble_tail .background {
    fill: #101010;
}

.tgme_widget_message_bubble_tail .border_1x {
    fill: #222;
}

.bc-comment-reply-content .bc-comment-text {
    color: #aaa;
}

.bc-comment-input-wrap .input.bc-comment-input,
.bc-comment-input-wrap .input.bc-comment-input:hover,
.bc-comment-input-wrap .input.bc-comment-input:active {
    background-color: #101010 !important;
    color: #fff !important;
    border-color: #222 !important;
}

.dropdown-menu {
    background: rgba(0, 0, 0, 0.7);
}

.bc-dropdown-wrap ul.dropdown-menu > li > .bc-dropdown-item {
    color: #eee;
    background: transparent;
}

.bc-dropdown-wrap ul.dropdown-menu > li > .bc-dropdown-item:hover {
    color: #eee;
    background: rgba(50, 50, 50, 0.5);
}

.bc-dropdown.dropdown-toggle:hover,
.open .bc-dropdown.dropdown-toggle {
    background-color: transparent;
    border: 1px solid rgba(100, 100, 100, 0.7);
}

.bc-comments-footer,
.bc-comment-footer {
    background: transparent;
}

.bc-comment-file-thumb {
    background-color: transparent;
}

.bc-arrow-down {
    background: transparent;
    box-shadow: 0 0 2px #000;
}
}
