css:
@-moz-document domain("nexojornal.com.br") {
body,
footer,
.navbar,
.container,
.fullWidth,
.fixhHeader {
    background-color: #222;
}

.columnists,
.academics,
.tribuna-home .columnists,
.secoes_1_teaser::before,
.secoes_3_teasers::before,
#imperdiveis_container::before,
#opinion-sidebox .columnists {
    background-color: #333;
}

#hits_container .kicker.see-more-kicker a::before,
#temas_container .kicker.see-more-kicker a::before {
    background-color: transparent;
}

.navbar {
    border: none;
}

.nexo-menu,
.trust-logo {
    filter: invert(100%);
}

.fixHeader {
    border-bottom: none;
}

h1,
h2,
h3,
.meta-author,
.linha-fina,
p,
li,
.author,
.footer-title,
.twitter-tweet,
.pullQuote,
.col-sm-12 {
    color: azure !important;
}

a {
    color: deepskyblue !important;
    text-decoration: none;
    box-shadow: none !important;
}

a:visited {
    color: chocolate !important;
}

/* links sociais */
.item {
    color: azure !important;
}

/* página inicial */
#imperdiveis_container,
.news-list .academics,
.secoes_1_teaser,
.secoes_3_teasers,
.row .grid-cell,
.big_teaser,
.row,
.teaser,
.secoes_1_teaser > div:nth-child(1) {
    background-color: #222 !important;
}

.title,
.academics {
    color: azure !important;
}
}

@-moz-document url-prefix("https://www.nexojornal.com.br/about/Perguntas-Frequentes") {
.panel-body,
#text {
    filter: invert(100%);
}

#heading11,
.panel-heading,
#heading11 > h4:nth-child(1) > a:nth-child(1) > p:nth-child(1) {
    color: #ccc !important;
}
}
