css:
@-moz-document domain(twitter.com)
{

	/*grey background, equal to right side box*/
	body {
		background-color: rgb(245, 248, 250) !important;
	}

	/* left pane */
	.r-o96wvk {
		width: 225px !important;
	}

    /*right pane with trends*/
    .r-1ovo9ad {
        width: 300px !important;
    }

    /*show more replies*/
    .r-kmv1fd {
        padding-bottom: 6px !important;
        padding-top: 6px !important;
    }

	/* left menu padding */
	.r-1ag2gil {
		padding-bottom: 4px !important;
		padding-top: 4px !important;
	}

	/* main tweet pane width */
	.r-1ye8kvj {
		max-width: 800px !important;
	}

	/*weird div that includes the tweet area and part of the right 
	.r-rthrr5 {
		width: auto !important;
	} */

	/*left side area flex number, 1 is even*/
	.r-obd0qt {
		flex-grow: 0.60 !important;   
	}

    /*set the flex grow to 0 here so the above tweet text won't go out of alignment from the above class that ends in qt*/
    .r-18kxxzh {
        flex-grow: 0 !important;
    }

	/*heading text smaller*/
	.r-adyw6z {
		font-size: 16px !important;
	}

	/*avatar*/
	.r-sdzlij {
		border-radius: 5px !important;
	}

	/*image box*/
	.r-t23y2h {
		border-radius: 8px !important;
	}

	/* retweet popup */
	.r-w0qc3r {
		padding-bottom: 6px !important;
		padding-top: 6px !important;
	}

	/*between text and like, retweet*/
	.r-a1ub67 {
		margin-top: 6px !important;
	}

	.r-psjefw {
		margin-top: 6px !important;
	}

	.r-1wqkr8a {
		padding-bottom: 0.50rem !important;
		padding-top: 0.50rem !important;
	}

	.r-l4ido4 {
		padding-bottom: 0.50rem !important;
	}

	/*thread padding*/
	.r-1k3wbuw {
		padding-top: 0.50rem !important;
	}

	/*show more*/
	.r-q81ovl {
		padding: 6px !important;
	}

	/*spacer div*/
	.r-76blku {
		height: 6px !important;
	}

	/*single tweet timestamp*/
	.r-1l7z4oj {
		padding-bottom: 6px !important;
	}

	/*content area*/
	.r-1pdl7sx {
		padding-bottom: 6px !important;
		margin-bottom: 6px !important;
	}

	/*single tweet button row, also sets height on suggested accounts on right pane*/
	.r-1h3ijdo {
		height: 55px !important;
	}

	/*single tweet numbers*/
	.r-1yzf0co {
		padding-top: 8px !important;
		padding-bottom: 8px !important;    
	}

	/*show more tweets bar*/
	.r-1szxp23 {
		min-height: 35px !important;
	}
}
