css:
@-moz-document url-prefix("https://www.w3schools.com/") {
.w3-white, .w3-hover-white:hover {
    color: #000 !important;
    background-color: #4a4a !important;
}

.w3-dark-grey, .w3-hover-dark-grey:hover, .w3-dark-gray, .w3-hover-dark-gray:hover {
    color: #fff!important;
    background-color: #4caf50c4!important;
}

.w3-main, #main {
    transition: margin-left .4s;
    background-color: #38d057;
}

.w3-light-grey, .w3-hover-light-grey:hover, .w3-light-gray, .w3-hover-light-gray:hover {
    color: #000!important;
    background-color: #1ac12c!important;
}

.gsc-search-button-v2, .gsc-search-button-v2:hover, .gsc-search-button-v2:focus {
    border-color: #13c513;
    background-color: #13c513;
    background-image: none;
    filter: none;
}

.topnav {
    position: relative;
    z-index: 2;
    font-size: 17px;
    background-color: #31bd2d;
    color: #f1f1f1;
    width: 100%;
    padding: 0;
    letter-spacing: 1px;
    font-family: "Segoe UI",Arial,sans-serif;
}

.w3-code {
    width: auto;
    background-color: #95d489;
    padding: 8px 12px;
    border-left: 4px solid #4CAF50;
    word-wrap: break-word;
}

h2 {
    color: white;
}

.top {
    position: relative;
    background-color: #1ac12c;
    height: 68px;
    padding-top: 20px;
    line-height: 50px;
    overflow: hidden;
    z-index: 2;
}

.w3-right.w3-hide-small.w3-wide.toptext {
    color: white;
}

.goog-te-gadget-simple {
    background-color: #38d057;
    border-left: 1px solid #38d057;
    border-top: 1px solid #38d057;
    border-bottom: 1px solid #38d057;
    border-right: 1px solid #38d057;
    font-size: 10pt;
    display: inline-block;
    padding-top: 1px;
    padding-bottom: 2px;
    cursor: pointer;
    zoom: 1;
    *display: inline;
}

.goog-te-gadget-simple .goog-te-menu-value {
    color: #fff;
}
}

@-moz-document url-prefix("https://www.w3schools.com/") {
/*********************************/
/*								 */
/*			 Editor				 */
/*								 */
/* HTML, CSS, JS, PHP, BOOTSTRAP */
/*		  Python & More	 		 */
/*								 */
/*********************************/

div#tryitLeaderboard {
    background-color: #1AC12C;
    margin-top: 10px;
}

.trytopnav {
    background-color: #31BD2D;
}

.trytopnav a {
    color: black;
}

#container {
    background-color: #31BD2D;
    width: 100%;
    overflow: auto;
    position: absolute;
    top: 138px;
    bottom: 0;
    height: auto;
}

.w3-green, .w3-hover-green {
    color: #fff!important;
    background-color: black!important;
}

.w3-green, .w3-hover-green:hover {
    color: #fff!important;
    background-color: black!important;
}

.CodeMirror {
    position: relative;
    overflow: hidden;
    background: #38d057;
}

#iframeResult, #iframeSource {
    background-color: #38d057;
    height: 100%;
    width: 100%;
}

#textareaCode {
    background-color: #38d057;
    font-family: consolas,"courier new",monospace;
    font-size: 15px;
    height: 100%;
    width: 100%;
    padding: 8px;
    resize: none;
    border: none;
    line-height: normal;
    overflow: auto;
    word-wrap: break-word;
}

.cm-s-default .cm-tag {
    color: white/*#170*/;
}

.cm-s-default .cm-bracket {
    color: #ef1d1d !important;
}

.cm-s-default .cm-property {
    color: #b319a0;
}

.cm-s-default .cm-keyword {
    color: #de1569/*#708*/;
}

.cm-s-default .cm-atom {
    color: #de1569/*#219*/;
}

.cm-s-default .cm-m-css.cm-variable {
    color: #de1569;
}

.cm-s-default .cm-number {
    color: #de1569/*#164*/;
}
}
