css:
@-moz-document url-prefix("https://www.w3schools.com/") {
.w3-border-green, .w3-hover-border-green:hover {
    border-color: #3003d2!important;
}

.w3schools-logo .dotcom {
    color: #514caf;
}

.w3-green, .w3-hover-green {
    color: #fff!important;
    background-color: #2c09ab!important;
}

.w3-white, .w3-hover-white:hover {
    color: #000 !important;
    background-color: #4e8fbb !important;
}

.w3-theme {
    color: #fff !important;
    background-color: #514caf !important;
}

.w3-dark-grey, .w3-hover-dark-grey:hover, .w3-dark-gray, .w3-hover-dark-gray:hover {
    color: #fff!important;
    background-color: rgba(81, 76, 175, 0.65)!important;
}

.w3-main, #main {
    transition: margin-left .4s;
    background-color: #3885d0;
}

.w3-light-grey, .w3-hover-light-grey:hover, .w3-light-gray, .w3-hover-light-gray:hover {
    color: #000!important;
    background-color: #3b99bb!important;
}

.gsc-search-button-v2, .gsc-search-button-v2:hover, .gsc-search-button-v2:focus {
    border-color: #13c513;
    background-color: #13c513;
    background-image: none;
    filter: none;
}

.w3-example {
    background-color: #3b99bb!important;
    padding: 0.01em 16px;
    margin: 20px 0;
    box-shadow: 0 2px 4px 0 rgba(0,0,0,0.16),0 2px 10px 0 rgba(0,0,0,0.12)!important;
}

.topnav {
    position: relative;
    z-index: 2;
    font-size: 17px;
    background-color: #514caf !important;
    color: #f1f1f1;
    width: 100%;
    padding: 0;
    letter-spacing: 1px;
    font-family: "Segoe UI",Arial,sans-serif;
}

.topnav .w3-bar a.active {
    background-color: #231ba9;
    color: #ffffff;
}

.w3-code {
    width: auto;
    background-color: #1f82d2;
    padding: 8px 12px;
    border-left: 4px solid #1f82d2;
    word-wrap: break-word;
}

h2 {
    color: white;
}

.top {
    position: relative;
    background-color: #4e8fbb !important;
    height: 68px;
    padding-top: 20px;
    line-height: 50px;
    overflow: hidden;
    z-index: 2;
}

.w3-right.w3-hide-small.w3-wide.toptext {
    color: white;
}

.goog-te-gadget-simple {
    background-color: #514caf;
    border-left: 1px solid #514caf;
    border-top: 1px solid #514caf;
    border-bottom: 1px solid #514caf;
    border-right: 1px solid #514caf;
    font-size: 10pt;
    display: inline-block;
    padding-top: 1px;
    padding-bottom: 2px;
    cursor: pointer;
    zoom: 1;
    display: inline;
}

.goog-te-gadget-simple .goog-te-menu-value {
    color: #fff;
}

.w3-green, .w3-hover-green {
    color: #fff!important;
    background-color: #0d2882!important;
}

div#google_translate_element {
    background-color: rgb(81, 76, 175);
}

#sidenav a.active {
    background-color: #231ba9;
    color: #ffffff;
}

.nextprev a.w3-right, .nextprev a.w3-left {
    background-color: rgba(81, 76, 175, 0.65)!important;
    color: #ffffff;
    border-color: rgba(81, 76, 175, 0.65)!important;
}

.w3-info {
    background-color: #b4c8ff;
    border-left: 6px solid #231ba9;
}

.w3-btn, .w3-btn:link, .w3-btn:visited {
    color: #FFFFFF;
    background-color: #514caf;
}

a:hover, a:active {
    color: #514caf;
}

#w3-exerciseform {
    background-color: #514caf;
    padding: 16px;
    color: #ffffff;
}

#w3-exerciseform .exercisewindow {
    background-color: #aec8ff;
    padding: 16px;
    color: #000000;
}

.gsc-search-button-v2, .gsc-search-button-v2:hover, .gsc-search-button-v2:focus {
    border-color: #495fbb;
    background-color: #495fbb;
    background-image: none;
    filter: none;
}
}

@-moz-document url-prefix("https://www.w3schools.com/") {
/*********************************/
/*								 */
/*			 Editor				 */
/*								 */
/* HTML, CSS, JS, PHP, BOOTSTRAP */
/*		  Python & More	 		 */
/*								 */
/*********************************/

div#tryitLeaderboard {
    background-color: #4E8FBB;
    margin-top: 10px;
}

.trytopnav {
    background-color: #514CAF;
    color: white;
}


.trytopnav a {
    color: white;
}

#container {
    background-color: #514caf;
    width: 100%;
    overflow: auto;
    position: absolute;
    top: 138px;
    bottom: 0;
    height: auto;
}

.w3-green, .w3-hover-green {
    color: #fff!important;
    background-color: black!important;
}

.w3-green, .w3-hover-green:hover {
    color: #fff!important;
    background-color: black!important;
}

.CodeMirror {
    position: relative;
    overflow: hidden;
    background: #1F82D2;
}

#iframeResult, #iframeSource {
    background-color: #1F82D2;
    height: 100%;
    width: 100%;
}

#textareaCode {
    background-color: #1F82D2;
    font-family: consolas,"courier new",monospace;
    font-size: 15px;
    height: 100%;
    width: 100%;
    padding: 8px;
    resize: none;
    border: none;
    line-height: normal;
    overflow: auto;
    word-wrap: break-word;
}
}
