css:
@-moz-document domain("twitter.com") {
    /* Box Shadow */
    .r-xnswec {
        box-shadow: rgba(0, 0, 0, 0.2) 0px 0px 15px, rgba(0, 0, 0, 0.15) 0px 0px 3px 1px;
        border: 1px solid #38444d;
    }

    /* Toolbar icons */
    
    .r-xyw6el {
        align-self: center;
        width: 100%;
        padding: 7px;
    }
    .r-8kz0gk {
        margin-top: 9px;
        margin-left: 5px;
    }
    .r-lwhw9o {
        margin-left: 0;
    }
    .r-88pszg {
        display: none;
    }

    /* Avatar */
    .r-1n0xq6e {
        margin-left: 20px;
    }

    /* User button */
    header[role="banner"] [role="button"]:not([aria-haspopup="true"]) {
        max-width: 58px;
    }

    header[role="banner"] [role="button"]:not([aria-haspopup="true"]) > div:not(:first-of-type) {
        display: none;
    }

    /* Messages & Video Overlay */
    .r-1mf7evn {
        top: auto;
        left: auto;
        margin-right: 6px;
        margin-bottom: -2px;
    }
    .r-1mf7evn.r-1hycxz {
        max-width: 423px;
        margin-bottom: 59px !important;
    }
    .r-1mf7evn.r-1hycxz,
    .r-1rttkqs {
        width: 423px !important;
    }
    .r-hvns9x {
        max-width: 423px !important;
    }

    .r-sb58tz {
        position: absolute !important;
        left: 0;
        padding-left: 15px;
        padding-right: 15px;
    }

    .r-1mf7evn.r-1hycxz .r-sb58tz {
        border-radius: 0 !important;
    }

    .r-8kz0gk {
        align-items: center;
    }

    /* Image Overlay Tweets Column */
    .r-1hycxz {
        width: 423px;
    }

    /* Modal */
    [aria-modal="true"] {
        box-shadow: rgba(0, 0, 0, 0.2) 0px 0px 15px, rgba(0, 0, 0, 0.15) 0px 0px 3px 1px;
        border: 1px solid #38444d;
        transform: translateZ(0px);
    }

    [aria-modal="true"] .r-sb58tz {
        width: 100%;
        max-width: 100%;
    }

    [role="listbox"] {
        background-color: #15202b !important;
    }

    /* Nav */
    header[role="banner"] {
        flex-grow: 0;
        width: 79px;
        z-index: 0;
    }

    header[role="banner"]:after {
        content: '';
        position: fixed;
        top: 0;
        left: 80px;
        width: 1px;
        height: 100%;
        background-color: #38444d;
    }

    .r-o96wvk {
        width: 100%;
    }

    nav[role="navigation"] .r-zv2cs0 {
        background-color: transparent;
    }

    nav[role="navigation"] span {
        display: none;
    }

		nav[role="navigation"] [data-testid="AppTabBar_Notifications_Link"] span {
        display: inline;
    }

    /* Footer Links */
    main[role="main"] nav[role="navigation"] span {
        display: inline-block;
    }

    .r-e7q0ms {
        display: none;
    }

    /* Middle and Right Columns */
    .r-rthrr5 {
        width: 100%;
    }

    /* New Tweets
div[role="status"] {
    position: fixed;
    top: 105px;
} */
    /* Tweet Bar */
    .r-sb58tz {
        position: fixed;
        top: 0;
        max-width: 100%;
        /* max-width: calc(100% - 471px); */
        min-width: 403px;
    }

    .r-gtdqiz {
        background: transparent;
        border-bottom: none;
    }

    .r-ahm1il {
        background-color: transparent;
    }

    .r-k5y9r7 {
        display: none;
    }

    .r-1444osr {
        width: 100%;
        margin-top: 0;
        padding-top: 18px;
        padding-left: 100px;
        padding-right: 100px;
        border-top: 1px solid #38444d;
        border-bottom: 1px solid #38444d;
        background-color: #15202b;
    }

    .r-1444osr > div:first-of-type {
        min-width: 600px;
        width: calc(100% - 471px);
        margin-left: auto;
        margin-right: auto;
    }

    .r-ou255f {
        width: 100%;
        max-width: 800px;
        margin-left: auto;
        margin-right: auto;
        padding-left: 0;
        padding-right: 0;
    }

    .r-ou255f[dir="auto"] {
        width: auto;
        padding-left: 10px;
        padding-right: 10px;
        margin-left: 10px;
    }

    /* Tweet Input */
    .DraftEditor-root {
        max-width: calc(100% - 13px);
        background-color: #0e1317;
        padding: 10px;
        margin-top: -10px;
        border: 1px solid #3d5466;
        border-radius: 22px;
    }

    .DraftEditor-editorContainer {
        padding-right: 30px;
    }

    div[data-testid="primaryColumn"] {
        position: relative;
        flex-shrink: 1;
        max-width: 100%;
    }

    div[data-testid="primaryColumn"]:before {
        content: '';
        position: fixed;
        top: 0;
        left: 80px;
        width: calc(100% - 514px);
        height: 100%;
    }

    div[data-testid="primaryColumn"] .r-yfoy6g:not([role="link"]) {
        background-color: transparent;
        width: 100%;
        max-width: 800px;
        margin-left: auto;
        margin-right: auto;
    }

    /* Article */
    div[data-testid="primaryColumn"] article[role="article"] .r-ou255f {
        width: auto;
    }

    /* Feed */
    .r-2llsf {
        align-items: flex-start;
    }

    .r-1ye8kvj {
        min-width: 404px;
        max-width: 800px;
    }

    .r-1ye8kvj[aria-labelledby="detail-header"] {
        width: calc(100% - 404px);
    }

    main[role="main"] div[data-testid="primaryColumn"] .r-1ye8kvj {
        margin-top: 15px;
    }

    /* Right Column */
    .r-1ovo9ad {
        width: 100%;
        max-width: 360px;
        padding-top: 12px
    }

    form[role="search"] {
        padding-left: 15px;
        padding-right: 15px;
    }

    /* Messages */
    .r-yfoy6g:not(.r-1ovo9ad) {
        background-color: #000;
    }

    .r-yfoy6g section[role="region"] {
        min-width: 404px;
        margin-left: 0;
        margin-right: 0;
        background-color: #15202b;
    }

    aside[role="complementary"] .r-ou255f {
        padding-left: 15px;
        padding-right: 15px;
        background-color: #15202b;
    }

    aside[role="complementary"] .r-aaos50 {
        padding-top: 10px;
        background-color: transparent;
    }

    aside[role="complementary"] .r-rs99b7 {
        padding-top: 0;
        border: 0;
    }

    aside[role="complementary"] .r-ou255f [role="button"] {
        padding-top: 0;
    }

    aside[role="complementary"] .r-ou255f [aria-haspopup="true"] {
        top: -11px;
        left: -34px;
    }

    section[role="region"] .r-sb58tz a[role="button"] {
        position: fixed;
        top: 7px;
        right: 5px;
        margin-top: 0;
    }

    .r-gfcssk {
        height: 0.6rem !important;
    }

    /* Sticky */
    .r-gtdqiz {
        transform: translateZ(0);
    }

    /* Notifications */
    div[data-testid="primaryColumn"] .r-yfoy6g > .r-sb58tz {
        max-width: 100%;
        border-bottom-left-radius: 10px;
        border-bottom-right-radius: 10px;
        background-color: #060809;
    }

    /* Timeline Clicked/Single Tweet */
    h1[role="heading"] ~ div {
        width: 100%;
        max-width: 800px;
        margin-left: auto;
        margin-right: auto;
    }

    /* Who to Follow */
    [data-testid="UserCell"] {
        width: 100%;
        max-width: 800px;
        margin-left: auto;
        margin-right: auto;
    }

    /* Heading */
    [role="heading"] {
        text-overflow: ellipsis;
        overflow: hidden;
    }
}
