css:
@-moz-document regexp("https://show.zoho.com/home/.*") {
body{
    background:#000;
}
.zs-leftpanel,.zs-lp-header,.zs-mobileapps-wrapper{
/*    background : #000;*/
        background : #141414;
    border-color: #1f1f1f;
    color:#fff;
}
h2, h3{
    color: #f1f1f1;
}
.zs-presentations-menu ul li, .zs-folder-menu ul li{
        color: #c7c7c8;
}
.zs-grid-panel .zs-pre-title input{
    color:#c7c7c8;
}
.zs-grid-panel li:not(.zs-import-progress):hover, .zs-grid-panel li.ui-listoptions-open{
    background:#202020;
    box-shadow:0px 0px 5px #000;
}
.show-svg-icon,.zs-listing-container .show-svg-icon-24{
    fill :#fff;
    color:#e25e49;
}
.zs-list-view-options button.active svg{
    fill:#e25e49;
}
.zmenu__item, .zmenu{
    background:#141414;
}
.zmenu{
    border-color:#1f1f1f;
}
.zmenu__text,.zs-list-sort .zs-label-text{
    color:#fff;
}
input[type="text"], input[type="password"]{
       color:#c7c7c8;
}
.zs-list-panel li:hover{
    background:#202020;
}
.zs-upload-btn{
     background: #141414;
    border-color: #1f1f1f;
    height: 23px;
}
.zs-last-modified-on, .zs-grid-panel .zs-pre-modified-on{
    color:#c9c9c9;
}
.zsl-searchbox input[type="text"]{
    color:#6f6c6c;
}
.announce-list-icon{
    border: 1px solid #1f1f1f;
    background: #000;
    box-shadow: 0 2px 5px 0 #1f1f1f;
}
.zsl-sort.zpopover{
    box-shadow:0 5px 10px rgba(0,0,0,.2);
    background: #141414;
    color: #c9c9c9;
    border-color: #1f1f1f;
}
.zpopover-arrow-bottom{
    border-bottom-color:#141414;
}
.zpopover-arrow-bottom:after{
     border-bottom-color:#141414;
}
.ui-template-action .ui-templatelist-btn{
color: #f1f1f1;
    background: #2d2c2c;
}
.zs-list-feedbackform{
   backgrounf:#202020;
}
.zs-list-feedbackform textarea{
    background: #000;
    border-color: #5c5c5c;
color:#fff;
}
.zs-list-feedback-buttons .zs-list-feedback-submit{
background: #e25e49;
    border-color: #000000;
}
.ui-presentation-loader{
    background: #000;
}
.ui-template-overlay{
    background: #000;
}
.ui-templates {
    border-radius: 3px;
}
.ui-comingsoon-title{
color:#fff;
}
.ui-comingsoon-description{
color : #c9c9c9;
}
.ui-templates img {
    border-radius: 3px;
}
}
