css:
@-moz-document domain(forums.fusionfalluniverse.com) {
  
  .post img {
      max-width: 100%;
  }
  
  body {
    color: #008b03;
  }
  
  .windowbg, #preview_body {
    color: #008b03;
	background-color: #000f0550;
  }
  
  .windowbg2 {
	color: #008b03;
	background-color: #000f0550;
  }
  
  .stickybg {
	background: #002440;
  }
  
  .stickybg2 {
	background: #003259;
  }
  
  td, th, tr {
    color: #008b03;
  }
  
  .table_list tbody.content td.info a.subject {
    color: #09f;
  }
  
  .bbc_link:link, .bbc_link:visited {
    border-bottom: none;
  }
}
