css:
@-moz-document regexp("http.*://.*trakt.tv.*")
{
* {
    scrollbar-color: #2a2c2e #1c1e1f;
}
input, select, textarea, .input-group-addon {
    background-color: #151515 !important;
    color: #555;
}
.huck-ster, #huck-ster-sidenav, #huck-ster-desktop-wrapper, #huck-ster-mobile-wrapper, .huck-ster-vip-square, .huckster, #huckster-sidenav, #huckster-desktop-wrapper, #huckster-mobile-wrapper, .huckster-vip-square, #huckster--sidenav, #huckster--desktop-wrapper, #huckster--mobile-wrapper, .huckster--vip-square, #huck-ster-vip-info, .huck-ster-dfp, #huck-ster-desktop-sidenav, .frame-wrapper #huck-ster-desktop-wrapper.visible, #hu-ck-ster-desk-top-wrapper, #hu-ck-ster-mob-ile-square-wrapper, #hu-ck-ster-desk-top-sidenav, #hu-ck-ster-sidenav, #hu-ck-ster-mob-ile-wrapper, #hu-ck-ster-vip-info {
    display: none !important;
}
body div.frame-wrapper div.frame-grid.visible, body div.sidenav-inner > div, body > div.top.light, body > div.middle.light, body > div.bottom.light, body > div.top.dark, body > div.middle.dark, body > div.bottom.dark
, body.episode > div > section#info-wrapper > div.container > div.row > div#overview > div:not(.row):not(#new-comment-wrapper):not(#activity):not(#comments):not(#actors):not(#lists):not(#popular-lists-wrapper):not(#recent-comments-wrapper)
, body.movies > div > section#info-wrapper > div.container > div.row > div#overview > div:not(.row):not(#new-comment-wrapper):not(#activity):not(#comments):not(#actors):not(#lists):not(#popular-lists-wrapper):not(#recent-comments-wrapper)
, body.shows > div > section#info-wrapper > div.container > div.row > div#overview > div:not(.row):not(#new-comment-wrapper):not(#activity):not(#comments):not(#actors):not(#lists):not(#popular-lists-wrapper):not(#recent-comments-wrapper):not(#recent-episodes):not(#seasons-episodes-sortable)
, body.people > div > section#info-wrapper > div.container > div.row > div.info > div:not(.row):not(#new-comment-wrapper):not(#activity):not(#comments):not(#actors):not(#lists):not(#popular-lists-wrapper):not(#recent-comments-wrapper):not(#recent-episodes):not(#seasons-episodes-sortable)
, body.users > div > section > div:not(.container)
, body.users > section#progress-wrapper > div.container > div.add-top
, body.users.comments > section:not(#cover-wrapper):not(.subnav-wrapper):not(#user-comments):not(#rating-items):not(#progress-wrapper):not(#history-items):not(#network-user-wrapper) > div:not(.container)
, body.users.lists > section:not(#cover-wrapper):not(.subnav-wrapper):not(#user-comments):not(#rating-items):not(#progress-wrapper):not(#history-items):not(#network-user-wrapper) > div:not(.container)
, body.users.ratings > section:not(#cover-wrapper):not(.subnav-wrapper):not(#user-comments):not(#rating-items):not(#progress-wrapper):not(#history-items):not(#network-user-wrapper) > div:not(.container)
, body.users.collection > section:not(#cover-wrapper):not(.subnav-wrapper):not(#user-comments):not(#rating-items):not(#progress-wrapper):not(#history-items):not(#network-user-wrapper) > div:not(.container)
, body.users.show_progress > section:not(#cover-wrapper):not(.subnav-wrapper):not(#user-comments):not(#rating-items):not(#progress-wrapper):not(#history-items):not(#network-user-wrapper) > div:not(.container)
, body.users.history > section:not(#cover-wrapper):not(.subnav-wrapper):not(#user-comments):not(#rating-items):not(#progress-wrapper):not(#history-items):not(#network-user-wrapper) > div:not(.container)
, body.users.network > section:not(#cover-wrapper):not(.subnav-wrapper):not(#user-comments):not(#rating-items):not(#progress-wrapper):not(#history-items):not(#network-user-wrapper) > div:not(.container) {
    display: none !important;
}
section, ul.pagination a, #progress-wrapper .row .main-info .seasons, .alert-no-data, #boxes-wrapper .row .light, .uvModule, .site-main, .screenContent {
    background: #111 !important;
}
.btn-default, .dropdown-menu, .dropdown-menu > li > a, #info-wrapper .affiliate-links .section a {
    background-color: #222;
    border-color: #333;
    color: #555;
}
.btn-default:hover, .btn-default:focus, .btn-default:active, .btn-default.active, .open > .btn-default.dropdown-toggle, .panel-default .panel-heading {
    background-color: #222;
    border-color: #444;
    color: #666;
}
.dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus {
    background-color: #333;
    color: #ed1c24;
}
ul.pagination a, ul.pagination .disabled a, #info-wrapper .info .actors-wrapper .posters ul li .character {
    color: #333;
}
.subnav-wrapper, #info-wrapper .sidebar .external li a {
    background: #222;
}
#info-wrapper .sidebar .external li a, .posters .poster, #info-wrapper .sidebar .poster, #info-wrapper .sidebar a.with-poster, .form-control, .panel-default, .input-group-addon, body.settings .notifications-table .table tbody > tr > td, body.authorized_applications .notifications-table .table tbody > tr > td, body.applications .notifications-table .table tbody > tr > td, body.widgets .notifications-table .table tbody > tr > td {
    border-color: #333 !important;
}
.summary-activity .tabs .tab.selected {
    background: #151515;
	border-color: #333;
    border-bottom-color: #151515;
}
.users-wrapper, .alert-info, body.branding #logo-downloads-wrapper .logos-wrapper .logo, body.branding #api-wrapper, body.branding #scrobblers-wrapper, body.branding #logo-downloads-wrapper {
	background: #151515;
	border-color: #333; 
}
.custom-list .list-info .info .overview[data-readmore][aria-expanded="false"] .shade {
    background-image: -webkit-linear-gradient(top,rgba(255,255,255,0) 0%,#111 100%);
    background-image: -o-linear-gradient(top,rgba(255,255,255,0) 0%,#111 100%);
    background-image: linear-gradient(to bottom,rgba(255,255,255,0) 0%,#111 100%);
}
.comment-wrapper.list .above-comment, .comment-wrapper .above-comment, .comment-wrapper.featured .above-comment, .btn-watch, .btn-collect, .btn-list, .btn-comment, #standard-features, body.apps #apps-icon-wrapper, .payment-options, #auth-form-wrapper, .checkin-modal, .uvBody, #faq-wrapper .faq-item, #team-wrapper .faq-item, .panel, #seasons-episodes-sortable .under-info .titles, #seasons-episodes-sortable .episode-stats {
    background: #151515;
}
h3.instructions {
    background: #1b1b1b;
}
.comment-wrapper, .comment-wrapper.featured {
    background: #121212;
}
#team-wrapper .team-pics img, body.branding #apps-wrapper.hero-wrapper div.full-screenshot {
	border-color: #222;
	-webkit-filter: brightness(30%); /* Safari */
	filter: brightness(30%);
}
img {
	-webkit-filter: brightness(80%); /* Safari */
	filter: brightness(80%);
}
body.branding #scrobblers-wrapper.hero-wrapper div.full-screenshot {
    -webkit-filter: brightness(8%); /* Safari */
	filter: brightness(8%);
}


body.settings .notifications-table .table tbody > tr:hover, body.authorized_applications .notifications-table .table tbody > tr:hover, body.applications .notifications-table .table tbody > tr:hover, body.widgets .notifications-table .table tbody > tr:hover {
	background: #252525;
    color: #444;
}
}
