/* ==UserStyle==
@name           ‚≠ê World To Build++ üî®
@namespace      Alyx
@version        2.0.0
@author         Alyx
==/UserStyle== */

@-moz-document domain('www.worldtobuild.com') {
	:root {
		--wtb-plus-text-color: #aaaaaa;
		--wtb-plus-background-color: #141414;
		--wtb-plus-panel-background-color: #1a1a1a;
		--wtb-plus-border-color: #323232;
	}

	html, body {
		color: var(--wtb-plus-text-color);
		background: var(--wtb-plus-background-color) !important;
		/* background: linear-gradient(rgba(0, 0, 0, 0.8), rgba(0, 0, 0, 0.8)), url('https://media3.giphy.com/media/NKEt9elQ5cR68/giphy.gif') !important;
		background-position: center center !important;
		background-size: cover !important;
		background-repeat: no-repeat !important;
		background-attachment: fixed !important; */
	}

	.default-reinvented-wtb-header {
		padding: 0 0 0 112px;
		height: 80px;
		line-height: 80px;
	}

	.default-reinvented-wtb-header .header-button {
		font-size: 18px;
	}

	.default-reinvented-wtb-header > div > div:nth-of-type(3)::before {
		display: inline-block;
		position: absolute;
		margin: 10px 0 10px -92px;
		background-image: url('https://cdn.worldtobuild.com/img/corporate/wtb-logo_main350x350.png');
		background-size: 60px;
		width: 60px;
		height: 60px;
		content: '';
		cursor: pointer;
		filter: hue-rotate(190deg);
	}

	.header-buttons {
		line-height: 80px;
	}
	
	.image-container {
		display: none;
	}
	
	.background-white {
		background-color: var(--wtb-plus-panel-background-color);
	}
	
	.default-background-container {
		background-color: var(--wtb-plus-panel-background-color);
	}
	
	.default-page-title {
		color: var(--wtb-plus-text-color);
	}
	
	.default-page-subtitle {
		color: var(--wtb-plus-text-color);
	}
	
	.dropdown-pane {
		background-color: var(--wtb-plus-panel-background-color);
		border: 2px solid var(--wtb-plus-border-color);
	}
	
	.default-header-dropdown-caret-left {
		border-right: 8px solid var(--wtb-plus-border-color);
	}
	
	.default-header-dropdown-caret-right {
		border-left: 8px solid var(--wtb-plus-border-color);
	}
	
	.wtb-dropdown-item {
		color: var(--wtb-plus-text-color);
		white-space: nowrap;
	}
	
	.wtb-dropdown-break {
		border-top: 2px solid var(--wtb-plus-border-color);
	}

	.pilot-player-notice {
		background-color: var(--wtb-plus-background-color);
		border: 2px solid #ff990066;
	}
	
	.default-search-bar {
		color: var(--wtb-plus-text-color);
		background-color: var(--wtb-plus-panel-background-color);
		border: 2px solid var(--wtb-plus-border-color);
	}
	
	select, [type='color'], [type='date'], [type='datetime-local'], [type='datetime'], [type='email'], [type='month'], [type='number'], [type='password'], [type='search'], [type='tel'], [type='text'], [type='time'], [type='url'], [type='week'], textarea {
		color: var(--wtb-plus-text-color);
		background-color: var(--wtb-plus-panel-background-color);
		border: 2px solid var(--wtb-plus-border-color);
	}
	
	select:focus, [type='color']:focus, [type='date']:focus, [type='datetime-local']:focus, [type='datetime']:focus, [type='email']:focus, [type='month']:focus, [type='number']:focus, [type='password']:focus, [type='search']:focus, [type='tel']:focus, [type='text']:focus, [type='time']:focus, [type='url']:focus, [type='week']:focus, textarea:focus {
		background-color: var(--wtb-plus-panel-background-color);
		border: 2px solid var(--wtb-plus-border-color);
		box-shadow: none;
	}
	
	.default-game-icon-container a, .default-game-icon-container a:active, .default-game-icon-container a:link, .default-game-icon-container a:visited {
		color: var(--wtb-plus-text-color);
	}
	
	.world-category-button {
		color: var(--wtb-plus-text-color);
	}
	
	.world-info-individual {
		background-color: var(--wtb-plus-background-color);
		border: 2px solid var(--wtb-plus-border-color);
	}
	
	.world-title-individual {
		white-space: nowrap;
		overflow: hidden;
		text-overflow: ellipsis;
	}
	
	.w2b-game-thumbnail {
		background: none !important;
	}
	
	.world-description-box {
		background-color: var(--wtb-plus-background-color);
		border: 2px solid var(--wtb-plus-border-color);
		border-bottom: none;
		border-radius: 6px 6px 0 0;
	}
	
	.world-info-box {
		background-color: var(--wtb-plus-background-color);
		border: 2px solid var(--wtb-plus-border-color);
		border-top: none;
		border-radius: 0 0 6px 6px;
	}
	
	.pagination li a {
		color: var(--wtb-plus-text-color);
		background-color: var(--wtb-plus-panel-background-color);
	}
	
	.pagination li a:hover {
		background-color: var(--wtb-plus-border-color);
	}
	
	.default-profile-user-title {
		color: var(--wtb-plus-text-color);
	}
	
	.default-profile-player-avatar {
		background-color: var(--wtb-plus-panel-background-color);
	}
	
	.default-profile-stat-container {
		background-color: var(--wtb-plus-panel-background-color);
	}
	
	.default-player-profileDescription {
		background-color: var(--wtb-plus-background-color);
	}
	
	.profile-user-button-primary {
		background-color: var(--wtb-plus-background-color);
	}
	
	.profile-user-button-primary[disabled] {
		border: 2px solid var(--wtb-plus-border-color);
	}
	
	.profile-user-button-report {
		background-color: var(--wtb-plus-background-color);
		border: 2px solid #ff6c6c;
	}
	
	#profile-dropdown > a > div {
		background-color: transparent !important;
	}
	
	.default-my-friends-container {
		background-color: var(--wtb-plus-background-color);
		border: 2px solid var(--wtb-plus-border-color);
		border-radius: 6px;
	}
	
	.profile-preview {
		color: var(--wtb-plus-text-color);
	}
	
	.default-player-avatar-circle, .default-player-avatar-circle-dashboard {
		background-color: var(--wtb-plus-background-color);
		border: 2px solid var(--wtb-plus-border-color);
	}
	
	.default-modal-profile-description {
		background-color: var(--wtb-plus-background-color);
	}
	
	.default-modal-player-badge {
		border: 2px solid var(--wtb-plus-border-color);
	}
	
	.wtb-me-inventory-frame {
		background-color: var(--wtb-plus-panel-background-color);
	}
	
	.wtb-me-character-frame > div > div:nth-of-type(2) > div > div:nth-of-type(2) > div:nth-of-type(1) > div:nth-of-type(1) > button {
		box-shadow: 3px 5px 15px rgba(0, 0, 0, 0.2) !important;
	}
	
	#current-avatar {
		background-color: var(--wtb-plus-background-color) !important;
	}
	
	.wtb-me-character-frame > div > div:nth-of-type(2) > div > div:nth-of-type(2) {
		background-color: var(--wtb-plus-panel-background-color) !important;
	}
	
	#avatar, #pose, #skin, #pet {
		background-color: var(--wtb-plus-panel-background-color) !important;
		border: 2px solid var(--wtb-plus-border-color);
		box-shadow: 3px 5px 15px rgba(0, 0, 0, 0.2) !important;
	}
	
	#avatar > .wtb-button, #pet > .wtb-button {
		background-color: var(--wtb-plus-background-color) !important;
		padding: 8px;
		border: none !important;
	}
	
	.my-creations-sidebar-title {
		color: var(--wtb-plus-text-color);
	}
	
	.my-creations-sidebar-container {
		border-top: 2px solid var(--wtb-plus-border-color);
	}
	
	.game-image-container {
		background: none;
	}
	
	.game-image-container > img {
		border-radius: 6px 6px 0 0;
	}
	
	.game-info-container {
		background-color: var(--wtb-plus-background-color);
		border: 2px solid var(--wtb-plus-border-color);
		border-radius: 0 0 6px 6px;
		overflow: hidden;
	}
	
	.game-info-container > a {
		display: block;
		white-space: nowrap;
		overflow: hidden;
		text-overflow: ellipsis;
	}
	
	.default-wtb-tab {
		background: var(--wtb-plus-panel-background-color);
	}
	
	.default-wtb-tab button {
		background-color: var(--wtb-plus-panel-background-color);
	}
	
	.default-wtb-tab button div {
		color: var(--wtb-plus-text-color);
		background-color: var(--wtb-plus-panel-background-color);
	}
	
	.wtb-tab {
		background-color: var(--wtb-plus-panel-background-color);
		padding: 15px;
		border: 2px solid var(--wtb-plus-border-color);
		border-bottom: none;
		border-radius: 6px 6px 0 0;
	}
	
	.wtb-tab-content {
		background: var(--wtb-plus-panel-background-color);
		border: 2px solid var(--wtb-plus-border-color);
		border-top: none;
		border-radius: 0 0 6px 6px;
		box-shadow: none;
	}
	
	.tablinks {
		color: var(--wtb-plus-text-color);
	}
	
	#notification-container {
		color: var(--wtb-plus-text-color);
		background-color: var(--wtb-plus-background-color);
		border: 2px solid var(--wtb-plus-border-color);
		border-radius: 6px;
	}
	
	.achievement-title, .achievement-description {
		color: var(--wtb-plus-text-color);
	}
	
	/* wth Brennan please make this easier */
	.large-10.medium-10.small-12 > div.medium-padding.background-white {
		border: 2px solid var(--wtb-plus-border-color);
		border-top: none;
		border-radius: 0 0 6px 6px;
	}
	
	.default-wtb-footer-container {
		background-color: var(--wtb-plus-panel-background-color);
	}
	
	.default-footer-copyright-text {
		color: var(--wtb-plus-text-color) !important;
	}

	/* TEMPORARILY DISABLED [[marquee]]*/
}

style_setting_options.value:
.wtb-item-button:hover > .wtb-shop-item-info:nth-of-type(1) {
    white-space: nowrap;
}

.wtb-item-button:hover > .wtb-shop-item-info:nth-of-type(1) > a {
	display: inline-block;
	padding-left: 120%;
    animation: marquee /*[[marqueeSpeed]]*/s linear infinite;
}

@keyframes marquee {
	0% {
		transform: translate(0, 0);
	}
	100% {
		transform: translate(-120%, 0);
	}
}

.wtb-item-button:hover > .wtb-shop-item-info:nth-of-type(1) {
    white-space: nowrap;
}

.wtb-item-button:hover > .wtb-shop-item-info:nth-of-type(1) > a {
    display: inline-block;
    padding-left: 120%;
    animation: marquee /*[[marqueeSpeed]]*/s linear infinite;
}

@keyframes marquee {
	0% {
		transform: translate(-120%, 0);
	}
	100% {
		transform: translate(0, 0);
	}
}
