css:
@-moz-document domain("twitter.com") {
/* Hi this is only my second theme please be kind thanks*/

/* mariomario720 */

.DashboardProfileCard-avatarLink {
    background-color: #fff;
    border-radius: 10%;
    margin: -30px 0 0 8px;
    padding: 1px;
    vertical-align: bottom;
}
.global-nav-inner {
    background-image: -webkit-linear-gradient(top,#fff,#f2f2f2);
    height: 46px;
}
.nav>li {
    color: #636d73;
}
.bird-topbar-etched {
    color: #42AAF4;
    cursor: pointer;
    font-size: 21px;
    margin-top: 14px;
    margin-left: -24px;
    display: inline-block;
    transition: all .25s ease-in-out;
    font-weight: bold;
}
.EdgeButton--primary, .EdgeButton--primary:focus {
    background: #43aaf4;
    background-image: linear-gradient(0deg, #3290d9, transparent);
    border: 1px solid #3f87c5;
}
.EdgeButton--primary:hover, .EdgeButton--primary:active {
    background-color: #1B95E0;
    border-color: rgba(0, 0, 0, 0.22);
}
.EdgeButton--secondary {
    background-color: #e1e8ed;
    background-image: linear-gradient(0deg, #f5f8fa, #fff);
    border: 1px solid #e1e8ed;
    color: #292f33;
}
.EdgeButton--secondary:hover {
    background-color: #e1e8ed;
    background-image: linear-gradient(0deg, #e1e8ed, #fff);
    border: 1px solid #e1e8ed;
    color: #292f33;
}
body {
    background-color: #f4f4f4
}
.DashboardProfileCard-avatarImage {
    border-radius: 10%;
    height: 72px;
    width: 72px;
    border: 2px solid #E2E2E2;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    color: #FFF;
}
.ProfileAvatar {
    background: #fff;
    border: 2px solid #E2E2E2;
    border-radius: 3%;
    height: 200px;
    position: relative;
    width: 200px;
}
.EdgeButton--tertiary {
    background-image: linear-gradient(0deg, #f5f8fa, #fff);
    border: 1px solid #e3ebef;
    color: #292f33;
    line-height: 22px;
    padding: 6px 11.94px;
}
.nav .session .dropdown-toggle {
    border-radius: 2px;
}
.ProfileNav-item--userActions {
    background-image: -webkit-linear-gradient(top,#fff,#f2f2f2);
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    height: 59px;
    padding: 12px 0 0 2px;
    position: absolute;
    right: 0;
    top: 0;
}
.ProfileCanopy-navBar {
    background-image: -webkit-linear-gradient(top,#fff,#f2f2f2);
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    height: 60px;
}
#global-tweet-dialog .modal-tweet-form-container, #Tweetstorm-dialog .modal-body {
    background-color: #1B95E0;
    background: rgba(103, 103, 103, 0);
}
.modal-header {
    background-image: -webkit-linear-gradient(top,#fff,#f2f2f2);
    border-bottom: 1px solid #e6ecf0;
    border-radius: 6px 6px 0 0;
    padding-top: 12px;
    padding-bottom: 12px;
}
.modal-container {
    background: rgba(0, 0, 0, 0.7);
}
.Gallery.with-tweet.without-tweet .Gallery-media {
    border-radius: 0px;
    padding-bottom: 0;
}
}
