css:
@-moz-document domain("www.memrise.com") {
:root {
  --col-lg: #f1f1f1;
  --col-lg-hvr: #eaeaea;
  --col-meet-the-natives: #79eae0;
  --col-learn-new-words: #33d452;
  --col-speed-review: #ff5742;
  --col-difficult-words: #ff9e29;
  --col-chatbot: #fec500;
  --col-listening-skills: #cc78dc;
}

/* <Global> */
body
{
    background-color: #f5f5f5;
}

#content {
    background-color: #f5f5f5!important;
}

.course-progress-box, .sidebar-box, #page-head
{
    box-shadow: 0px 0px 3px 0px rgba(172, 172, 172, 0.35)!important;
    -webkit-box-shadow: 0px 0px 3px 0px rgba(172, 172, 172, 0.35)!important;
    -moz-box-shadow: 0px 0px 3px 0px rgba(172, 172, 172, 0.35)!important;
}
/* </Global> */

/* <Course Box> */
.course-card-container .goal-setter {
    color: #2B3648;
    background: #f5f5f5;
    -webkit-box-shadow: inset 0px 0px 4px 0px rgba(0,0,0,0.1);
    -moz-box-shadow: inset 0px 0px 4px 0px rgba(0,0,0,0.1);
    box-shadow: inset 0px 0px 4px 0px rgba(0,0,0,0.1);
}
.course-card-container .course-progress-box .card-top .card-main-container .simple-btn {
    border-radius: 20px;
    padding: 6px;
    background: #f5f5f5;
    font-size: 14px;
    color: #2b3648;
    text-align: center;
    background-color:red!important;
    line-height: 14px;
}
.course-card-container .course-progress-box .card-top .card-main-container .goal-setter-btn .orange, .course-card-container .course-progress-box .onoff .pts {
    color: #2b3648;
}

.mode-selector-button.mode-selector-button {
    background-color: #ffffff!important;
}
.mode-selector-button.mode-selector-button:hover {
    background-color: var(--col-lg)!important;
}

.single-continue-button {
    min-width: 50px!important;
    text-decoration: none!important;
}

.single-continue-button-text {
    font-family: "Open Sans",Sans-Serif!important;
    font-size: 16px!important;
    white-space: nowrap!important;
    text-transform: inherit!important;
    padding-right: 14px!important;
}

.course-card-container .course-progress-box .card-top .card-main-container .course-progress .left .course-complete-text {
    background: white!important;
    border-radius: 0px!important;
    color: #636363!important;
}

.course-card-container .course-progress-box .onoff, .course-card-container .course-progress-box .points {
    color: #313130!important;
}
.course-card-container .course-progress-box .card-top .card-main-container .simple-btn {
    border-radius: 4px!important;
    background: var(--col-lg)!important;
}
.course-card-container .course-progress-box .card-top .card-main-container .simple-btn:hover {
    background: var(--col-lg-hvr)!important;
}

.course-card-container .course-progress-box .card-top .card-main-container .simple-btn.leaderboard-btn {
    background-color: var(--col-lg)!important;
}
.course-card-container .course-progress-box .card-top .card-main-container .simple-btn.leaderboard-btn:hover {
    background-color: var(--col-lg-hvr)!important;
}

.course-card-container .card-counter div svg {
    top: 60%!important;
}

.single-continue-button-icon {
    width: 42px!important;
    height: 42px!important;
    top: -7px!important;
    left: -7px!important;
}

.card-counter-container .card-counter
{
    padding: 0px!important;
}
.card-counter-container .card-counter
{
    background-color: var(--col-lg)!important;
    margin: 0px 7px 0px 7px!important;
    padding: 0px 7px 0px 9px!important;
    border-radius: 10%!important;
}

.single-continue-button.single-continue-button {
    border-radius: 5px!important;
    -webkit-box-shadow: 0px 0px 3px 0px rgba(181, 181, 181, 0.55);
    -moz-box-shadow: 0px 0px 3px 0px rgba(181, 181, 181, 0.55);
    box-shadow: 0px 0px 3px 0px rgba(181, 181, 181, 0.55);
}
.single-continue-button.single-continue-button:hover {
    border-radius: 5px!important;
    -webkit-box-shadow: 0px 0px 3px 0px rgba(181, 181, 181, 0.55);
    -moz-box-shadow: 0px 0px 3px 0px rgba(181, 181, 181, 0.55);
    box-shadow: 0px 0px 3px 0px rgba(181, 181, 181, 0.55);
}

.single-continue-button.meet-the-natives {
    background-color: var(--col-meet-the-natives)!important;
}
.single-continue-button.learn {
    background-color: var(--col-learn-new-words)!important;
}
.single-continue-button.speed-review {
    background-color: var(--col-speed-review)!important;
}
.single-continue-button.difficult-words {
    background-color: var(--col-difficult-words)!important;
}
.single-continue-button.pro-chat {
    background-color: var(--col-chatbot)!important;
    color: #3e3e3e!important;
}
.single-continue-button.listening-skills {
    background-color: var(--col-listening-skills)!important;
}

.course-card-container .course-progress-box .card-top .card-image-col .img-crop {
    border-radius: 5%!important;
}

.course-card-container .course-progress-box .card-top .card-main-container .course-progress .progress-bar-container .progress-bar {
    border-radius: 5px!important;
    background-color: #FFC000;
}
/* </Course Box> */


/* <Page Header> */
.header-nav .header-nav-item:after {
    height: 0.250rem;
    -webkit-transform: rotate(-0deg)!important;
    transform: rotate(-0deg)!important;
    background-color: #5f6a7d!important;
    border-radius: 1px!important;
}
/* </Page Header> */

/* </Sidebar> */
.sidebar-profile .profile-content .content-stats .left, .sidebar-profile .profile-content .content-stats .right,
.countdown-body .countdown-duration .time-block
{
    background: var(--col-lg)!important;
        -webkit-box-shadow: inset 0px 0px 2px 0px rgba(0,0,0,0.1);
    -moz-box-shadow: inset 0px 0px 2px 0px rgba(0,0,0,0.1);
    box-shadow: inset 0px 0px 2px 0px rgba(0,0,0,0.1);
}

.sidebar-profile .profile-header .image-wrapper img
{
    border-radius: 50%!important;
}

.sidebar-main-btn .simple:hover, .sidebar-main-btn .simple:active {
    text-decoration: none;
    background-color: #e9edef!important;
}

.stats-breakdown
{
    margin-top:12px;
    padding-bottom:10px;
}
.stats-breakdown div
{
    background: #dcf2f9!important;
}
.stats-breakdown div:first-child
{
    margin-left: 5px!important;
}
.stats-breakdown div:last-child
{
    margin-right: 5px!important;
}
/* </Sidebar> */

/* <Session> */
.mode-selector-modal-content .mode-icons .mode-icon-wrapper:hover .mode-icon-label {
    color: #fff!important;
}
.mode-selector-modal-content .mode-icons .mode-icon-wrapper .mode-icon-label {
    color: #565d6a!important;
}
.mode-selector-modal-content .mode-icons .mode-icon-wrapper {
    text-decoration: none!important;
}

.mode-selector-modal-content .mode-icons .mode-icon-wrapper .learn.enabled {
    background-color: var(--col-learn-new-words)!important;
}
.mode-selector-modal-content .mode-icons .mode-icon-wrapper .classic_review.enabled {
    background-color: #FFC000!important;
}
.mode-selector-modal-content .mode-icons .mode-icon-wrapper .speed_review.enabled {
    background-color: var(--col-speed-review)!important;
}
.mode-selector-modal-content .mode-icons .mode-icon-wrapper .difficult_words.enabled {
    background-color: var(--col-difficult-words)!important;
}
.mode-selector-modal-content .mode-icons .mode-icon-wrapper .listening_skills.enabled {
    background-color: var(--col-listening-skills)!important;
}
.mode-selector-modal-content .mode-icons .mode-icon-wrapper .meet_the_natives.enabled {
    background-color: var(--col-meet-the-natives)!important;
}
.mode-selector-modal-content .mode-icons .mode-icon-wrapper .pro_chat.enabled {
    background-color: var(--col-chatbot)!important;
}

.leaderboard-container.wide .leaderboard .leaderboard-rows .leaderboard-row .row-pic img {
    border-radius: 50%!important;
}

.garden-box .shiny-box {
    border-radius: 6px!important;
    font-size: 20px!important;
}

.garden-box .shiny-box.active {
    border: 1px solid #3c8666!important;
}

 /* Word Selection (Selected) */
.btn.active, .btn:active, button.active, button:active, input[type="button"].active, input[type="button"]:active, input[type="reset"].active, input[type="reset"]:active, input[type="submit"].active, input[type="submit"]:active, button[type="submit"].active, button[type="submit"]:active {
    background-color: #e8e8e8!important;
}

 /* Word Selection */
.btn
{
    -webkit-box-shadow: 0px 0px 3px 0px rgba(181, 181, 181, 0.55);
    -moz-box-shadow: 0px 0px 3px 0px rgba(181, 181, 181, 0.55);
    box-shadow: 0px 0px 3px 0px rgba(181, 181, 181, 0.55);
}
/* </Session> */
}
