}
.f1 {
    box-shadow: 0px 10px 16px 0 rgba(73, 39, 74, 255);
    border: none;
    border-radius: 2rem;
}
.f1 h3 {
    font-size: 27px;
    font-weight: 600;
    color: #ffab00;
        -webkit-animation: glow1 5s ease-in-out infinite alternate;
    -moz-animation: glow1 5s ease-in-out infinite alternate;
    animation: glow1 5s ease-in-out infinite alternate
}
.f1 i {
    background: #58585800;
    width: 65px;
    height: 65px;
    line-height: 65px;
    text-align: center;
    font-size: 30px;
}
.card {
    box-shadow: 0px 10px 16px 0 rgba(73, 39, 74, 255);
    border: none;
}
.badge-success {
    color: #666666;
    background-color: #262626;
    -webkit-animation: glow3 5s ease-in-out alternate;
    -moz-animation: glow3 5s ease-in-out alternate;
    animation: glow3 5s ease-in-out alternate; 
}
#addbutton {
    margin-top: -10px;
    width: auto;
    margin-left: 33px;
    background-color: #000000;
}
.phone-icon-inner {
    color: #49274a;
	text-shadow: 0 0 10px #000, 0 0 10px #49274a, 0 0 10px #49274a, 0 0 10px #49274a, 0 0 10px #49274a;

}
.list-group-item {
    position: relative;
    display: block;
    padding: 1rem 1.5rem;
    margin-bottom: -1px;
    background-color: #fff0;
    border: 1px solid #ffab0000;

}
.card-header {
    border-bottom: 1px solid #ffab00;
}
.h1, .h2, .h3, .h4, .h5, .h6, h1, h2, h3, h4, h5, h6 {
    margin-bottom: .5rem;
    font-family: inherit;
    font-weight: 500;
    line-height: 1.3;
    color: #7597c3;
}

.dialbannerstatus {
    font-size: 32px;
    font-weight: bold;
    text-align: center;
    padding-left: 0;
    padding-right: 0;
    height: 70px;
	border-style: solid;
    border-width: 6px;
    border-radius: 20px;
    margin-left: 250px;
    margin-right: -45px;

    -webkit-animation: glow1 1s ease-in infinite alternate;
    -moz-animation: glow1 1s ease-in infinite alternate;
    animation: glow1 1s ease-in infinite alternate;

	color: #ffab00
                                            
}
.dialbannertext {
    text-align: center;
    font-size: 68px;
    padding: 0 0 0 15vw;
    color: #884f67;
    height: 110px;
    margin: -10px;
	-webkit-animation: glow2 5s ease-in-out infinite alternate;
	-moz-animation: glow2 5s ease-in-out infinite alternate;
    animation: glow2 5s ease-in-out infinite alternate;
}
/*=================================================GLOW SETUP #1================================================================================*/
@-webkit-keyframes glow1 {
  from {
    text-shadow: 0 0 10px #000, 0 0 10px #ffab00, 0 0 10px #ffab00, 0 0 10px #ffab00, 0 0 10px #ffab00, 0 0 10px #ffab00, 0 0 10px #ffab00;
  }
  to {
    text-shadow: 0 0 20px #000, 0 0 30px #662c4400, 0 0 40px #662c4400, 0 0 50px #662c4400, 0 0 60px #662c4400, 0 0 70px #662c4400, 0 0 80px #662c4400;
  }
}
/*=================================================GLOW SETUP #2=========(800 NUMBERS)=========================================================*/
@-webkit-keyframes glow2 {
  from {
    text-shadow: 0 0 10px #000, 0 0 10px #000, 0 0 10px #000, 0 0 10px #000, 0 0 10px #000, 0 0 10px #000, 0 0 10px #000;
  }
  to {
    text-shadow: 0 0 20px #000, 0 0 30px #662c4400, 0 0 40px #662c4400, 0 0 50px #662c4400, 0 0 60px #662c4400, 0 0 70px #662c4400, 0 0 80px #662c4400;
  }
}
/*=============================================================================================================================================*/
@-webkit-keyframes glow3 {
  from {
    text-shadow: 0 0 10px #000, 0 0 10px #36b37e, 0 0 10px #36b37e, 0 0 10px #36b37e, 0 0 10px #36b37e, 0 0 10px #36b37e, 0 0 10px #36b37e;
  }
  to {
    text-shadow: 0 0 20px #000, 0 0 30px #00000000, 0 0 40px #00000000, 0 0 50px #00000000, 0 0 60px #00000000, 0 0 70px #00000000, 0 0 80px #00000000;
  }
}
/*=============================================================================================================================================*/
@-webkit-keyframes glow4 {
  from {
    text-shadow: 0 0 10px #000, 0 0 10px #36b37e, 0 0 10px #36b37e;
  }
  to {
    text-shadow: 0 0 20px #000, 0 0 30px #00000000, 0 0 40px #00000000;
  }
}
/*=============================================================================================================================================*/

@-webkit-keyframes glow5 {
  from {
    text-shadow: 0 0 10px #000, 0 0 10px #49274a, 0 0 20px #49274a, 0 0 30px #49274a, 0 0 40px #49274a, 0 0 50px #49274a, 0 0 60px #49274a;
  }
  to {
    text-shadow: 0 0 20px #000, 0 0 30px #00000000, 0 0 40px #00000000, 0 0 50px #00000000, 0 0 60px #00000000, 0 0 70px #00000000, 0 0 80px #00000000;
  }
}
/*=============================================================================================================================================*/
