css:
@-moz-document domain('restframework.herokuapp.com'),domain('django-rest-framework.org'){
body, .toclink, .promo li a, blockquote, .well {
  color: #EEE;
  background-color: #333;
}

body a {
  color: #eaeaea;
   
}

body a:hover {
    color: #cccccc;
}

.chart {
  color: #333;
}

.nav-list > li > a, .nav-list .nav-header {
  text-shadow: 0 1px 0 rgba(255, 255, 255, 0.1);
}

pre { color: #A8A8Ac; }

.com { color: #93a1a1; }
.lit { color: #e95f91; }
.pun, .opn, .clo { color: #93a1a1; }
.fun { color: #3cd2df; }
.str, .atv { color: #9FEAaF; }
.kwd, .prettyprint .tag { color: #fec48b; }
.typ, .atn, .dec, .var { color: #F08080; }
.pln { color: #A8A8Ac; }

.prettyprint {
  padding: 8px;
  background-color: #272729;
  border: 1px solid #111118;
}
.prettyprint.linenums {
  -webkit-box-shadow: inset 40px 0 0 #fbfbfc, inset 41px 0 0 #ececf0;
     -moz-box-shadow: inset 40px 0 0 #fbfbfc, inset 41px 0 0 #ececf0;
          box-shadow: inset 40px 0 0 #fbfbfc, inset 41px 0 0 #ececf0;
}

/* Specify class=linenums on a pre to get line numbering */
ol.linenums {
  margin: 0 0 0 33px; /* IE indents via margin-left */
}
ol.linenums li {
  padding-left: 12px;
  color: #bebec5;
  line-height: 20px;
  text-shadow: 0 1px 0 #fff;
}

code {
  padding: 2px 4px;
  color: #f37c05;
  background-color: #272729;
  border: 1px solid #111118;
}

/*Append by mehran-prs*/
a.toclink:hover{
	color:#2196f3;
}

.well{
	border-color:#4e4e4e;
}

.navbar .navbar-inner{
	border-top-color:#ff483a;
}

.btn-inverse{
	background: #3e3c3c!important;

    box-shadow: none;
}
.btn-inverse:hover{
	background: #4c4b4b!important;
}

.nav > li > a:hover {
    background-color: #636161;
}

/* Fix code color */
.h1 code, h2 code, h3 code, h4 code, h5 code{
    color:white;
}
}
