css:
@-moz-document domain(wunderlist.com) {
#wunderlist-base {
    color: #ddddd !important;
    background-color: #111111 !important; 
    background-image: none !important;
}

#wunderlist-base.application-main.background-13:before {
    color: #ddddd !important;
    background-color: #111111 !important; 
    background-image: none !important;
}

#lists {
    background-color: #222222 !important;    
}

.sidebarActions {
      background:#313131 !important;  
}

.background-13 #search-toolbar {
    background: #003399 !important;
}

.background-13 #list-toolbar {
    background: #003399 !important;
}

.background-13#wunderlist-base .addTask {
    background: rgba(74, 81, 156, 0.75) !important;
}

.background-13#wunderlist-base #tasks .heading a {
    background: rgba(74, 81, 156, 0.75) !important;
}

.taskItem-body {
    background: #222222 !important;
}

.taskItem-titleWrapper {
    color: #dddddd !important;   
}

.taskItem-duedate {
    color: grey !important;
}

svg * {
    stroke: grey !important;
}

span.user-name {
    color: #0f0 !important;
}

h1.title {
    color: #fff !important;  
    font-weight: 400 !important;
}

.checkBox .detail-check, .checkBox svg.task-check {
    stroke: rgba(232, 232, 232, 0.35);
}

.sidebarItem.active a {
    background-color: #333333 !important;
}

.sidebarItem.active a .count {
    color: #dddddd !important;
}

ul.filters-collection li.active a span.title {
    color: #fff !important;
    font-weight: 900;
}

ul.filters-collection li.active a:hover span.title {
    color: #99f !important;
    font-weight: 900;
}

ul.filters-collection li.sidebarItem a span.title {
    color: #fff !important;
}

ul.filters-collection li.sidebarItem a:hover span.title {
    color: #99f !important;
}

ul.lists-collection li.sidebarItem a span.title {
    color: #ccc !important;
}

ul.lists-collection li.sidebarItem a:hover span.title {
    color: #99f !important;
}

span.overdue-count:not(:empty) {
  display: block !important;
  height: 30px !important;
  width: 30px !important;
  line-height: 30px !important;

  -moz-border-radius: 15px !important; /* or 50% */
  border-radius: 15px !important; /* or 50% */

  background-color: red !important;
  color: white !important;
  text-align: center !important;
  font-size: 1em !important;
}

#detail .inner {
    background-color: #333333 !important;
}

#detail .body {
    background-color: #333333 !important;
}

#wunderlist-base .comments-bottom {
    background-color: #333333 !important;
}

#wunderlist-base .comments-add .input-fake {
    background: #333333 !important;
}

#wunderlist-base .last-comment {
    background: #333333 !important;
}

#wunderlist-base textarea {
    background-color: #333333 !important;
}

#wunderlist-base .popover {
    background-color: #333333 !important;
}

#wunderlist-base .popover .list-menu li a {
    color: #dddddd !important;
    text-shadow: none !important;
}
}
