css:
@-moz-document domain(slashdot.org) {

body {
    background: #222;
}
body > section {
    background: #222;
}
.container {
    background-color: #333;
    color: #ddd;
}
.main-content {
    background-color: #333;
    color: #ddd;
}
.article .body .p > i {
    background-color: #333;
    color: #ddd;
}
.article:not(.fhitem-journal) .body .p {
    background-color: #333;
    color: #ddd;
}
#firehose article header .details {
    background-color: #4d4d4d;
    color: #f2f2f2;
}
a:visited, a:hover {
    color: #42dcf4;
}
a, .link, a.link {
    color: #42dcf4;
}
#slashboxes section {
    background-color: #4d4d4d;
    color: #f2f2f2;
}
.message-bar {
    background-color: #4d4d4d;
    color: #e5e5e5;
}
.poll-group-form label {
    background-color: #6d6d6d;
}
.poll-group-form label:hover {
    background-color: #bbb;
}
.thisday-tb tr td {
    background: none repeat scroll 0% 0% #5D5D5D;
    border: 2px solid #8a7474;
    border-top-color: rgb(230, 230, 230);
}
#slashboxes article {
    border-color: #854141;
}
.poll-group-form {
    padding: 0px 5px;
}
.units-6 {
    width: 95%;
}
#d2header {
    background: rgb(57, 57, 57);
}
body.slashdot-it li.comment.full .details, #comments .details {
    background: none repeat scroll 0 0 #262626;
    color: #8A8A8A;
}
.slashdot-it #commentlisting li.comment.full, #comments .full {
    border: 1px solid rgb(57, 8, 57);
}
#newa2footerv2 .ns h3 a, #newa2footerv2 .pr h3 a {
    background-color: #484646;
}
#newa2footerv2 .rl h3 {
    background-color: #484646;
    color: #ccc;
}
body.slashdot-it li.comment.full .details, #comments .details {
background: none repeat scroll 0 0 #636363;
color: #E5E5E5;
}
a:visited, a:hover {
    color: rgb(2, 205, 205);
}
a, .link, a.link {
color: rgb(2, 163, 163);
}
#newa2footerv2 .pr h3, #newa2footerv2 .rl h3, #newa2footerv2 .ns h3 {
    background: #484646;
}
body {
    background: rgb(71, 71, 71);
}#firehose article header .details {
    color: #f2f2f2;
    background: #4d4d4d;
}
body > section {
    background: #2c2c2c;
}
.message-bar {
    color: #e5e5e5;
    background-color: #7d7d7d;
    border-bottom: 1px solid #b80000;
}
#d2header {
    background: rgb(42, 42, 42);
}
blockquote.msg > p {
	background: rgb(93, 92, 92);
	color: rgb(208, 208, 208);
}
.grid_14 {
	color: #858585;
}
a:visited, a:hover {
    color: rgb(aa,aa,aa);
}
label {
    color: #aaa;
}
legend {
    color: #aaa;
}
p {
    color: #aaa;
} 
.notes {
    color: #aaa;
} 

}
