css:
@-moz-document domain(www.twitch.tv) {

.channel-root {
    background-color: #222;
}
.tw-mg-x-3 {
    background-color: #222;
}
.tw-flex-shrink-0 {
    background-color: #222;
    color: #ccc;
}
.tw-flex-shrink-1 {
    background-color: #282828;
}
.tw-link:visited, .tw-root--theme-light .tw-link:focus {
    color: #905fea;
}
.tw-pd-x-1 {
    background-color: #282828;
    color: #ccc;    
}
body {
    background: #222;
    color: #ccc;
}
.tw-core-button--dropdown {
    background-color: #522b99;
    color: #ccc;
}
.tw-select-button {
    background: #555;
    border: 1px solid #000;    
    color: #ccc;
}
.tw-flex-row {
    background-color: #333;
}
.tw-c-text-base {
    color: #ddd!important;
}
.tw-core-button--secondary {
    color: #aaa;
}
.tw-c-text-alt {
    color: #ccc !important;
}
.tw-c-background-alt {
    background-color: #222 !important;
}
.tw-textarea {
    background: #433f4a;
    border: 1px solid #dad8de;
    color: #eee;
}
.tw-link {
    /* color: #6441a4; */
    color: #7f66ac;
}
.carousel-metadata {
    background: #333;
}
.carousel-metadata--fadeout {
    background: linear-gradient(180deg,hsla(0, 0%, 0%, 0) 0,hsla(0, 0%, 28.2%, 0.9) 75%,#424141 80%,#333);
}
.tw-tag__content {
    background-color: #333;
}
.tw-checkbox .tw-checkbox__label {
    color: #a89db9;
}


}
