css:
@-moz-document domain(docs.unity3d.com) {
/****************************************
  ==== BODY
****************************************/

  body {
    background-color: #161616;
  }

/****************************************
  ==== MESSAGES
****************************************/

  div.message-error {
    background: #fd4b23;
  }

  div.message-warning {
    background: #fdaf23;
  }

  div.message-ok {
    background: #27c314;
  }

/****************************************
  ==== TYPO
****************************************/

  body {
    color: #fff;
  }

  h1 {
    color: #fff;
  }

  h2 {
    color: #fff;
  }

  div.subsection div.subsection h2 {
    /*color: #455463;*/
    color: #e6e6e6;
  }

  h3 {
    color: #fff;
  }

  h4 {
    color: #fff;
  }

  a, a:hover, a:focus, a:active {
    color: #00b0ff;
  }

  a[href^="http://"], a[href^="https://"] {
    background: none;
    padding-right: 0;
    background-size: 0;
  }

  code {
    color: #fbe707;
    background-color: #161616;
  }

/****************************************
  ==== HEADER
****************************************/

  div.header a {
    color: #fff;
  }

  div.header .more .filler {
    background: #222c37;
  }

  div.header div.search-form:before, div.header .more .filler:before {
    background: none;
  }

  div.header div.search-form input.field {
    background: #000;
  }

  div.header div.search-form input.field:focus {
    background: #fff;
  }

/****************************************
  ==== TOOLBAR
****************************************/

  div.toolbar {
    background: #6d43af;
    color: #fff;
    border-bottom: #222c37 2px solid;
    ;
  }

  div.lang-switcher div.lang-list {
    background: #222c37;
  }

  div.lang-switcher div.lang-list a {
    color: #00b0ff;
  }

  div.otherversionscontent {
    background-color: #222c37;
    border-top: none;
  }

  div.otherversionscontent a {
    color: #00b0ff;
  }

  li.vsDivHeader {
    border-left: 8px solid #222c37;
  }

  .vsDivHeader, #otherVersionsLegend {
    background-color: #222c37 !important;
  }

/****************************************
  ==== FOOTER
****************************************/

  div.footer {
    background-color: #222c37;
    border-radius: 2em;
    border-top: none;
    padding-left: 1em;
    padding-right: 1em;
  }

  div.footer div.copy a {
    color: #fff;
  }

  div.footer div.menu a {
    color: #fff;
  }

/****************************************
  ==== SIDEBAR
****************************************/

  div.sidebar-wrap {
    background-color: #161616;
  }

  div.sidebar-wrap {
    border-right: #e6e6e6;
  }

  div.sidebar-menu h2 {
    border-bottom: #e6e6e6 1px solid;
  }

  div.sidebar-menu ul li div.leafnode {
    border: 0;
    position: absolute;
    background-color: #161616;
  }

  div.sidebar-menu ul li div.arrow, div.sidebar-menu ul li div.node {
    border-color: #161616;
    background-color: #7043af;
  }

  div.sidebar-menu ul li div.leafnode.expanded {
    background: none;
  }

  div.sidebar-menu ul li a {
    color: #00b0ff;
  }

  div.sidebar-menu ul li a.current {
    background: none;
    color: #ffaa00;
  }

  div.sidebar-wrap .nl span {
    color: #46d96f;
  }

/****************************************
  ==== CONTENT
****************************************/

  .nextprev {
    background: #6d43af;
  }

  .content .section ul li:before {
    color: #46d96f;
  }

  .content h4 {
    color: #70a5ca !important;
  }

  .content div.frontpage-section h3 a {
    color: #00b0ff;
  }

  .content figure figcaption {
    color: #99cc88;
  }

  .content table thead th {
    background: #222c37 !important;
  }

  .content table tbody tr {
    background: #222 !important;
  }

  .timetoshineitem {
    background: none;
  }

  table.list tr:hover {
    outline: #46d96f 1px solid;
  }

  .subsection .list tbody tr {
    background-color: #222c37 !important;
    border: #9e488d 1px solid;
    color: #fff;
  }

/****************************************
  ==== Feedback FORM
****************************************/

  .feedbackbox {
    background: #222c37 !important;
    padding: 1em;
    border-radius: 1em;
  }

/****************************************
  ==== BREADCRUMBS
****************************************/

  .breadcrumbs ul li:after {
    color: #ffaa00;
  }

  .breadcrumbs ul li a {
    color: #00b0ff;
  }

/****************************************
  ==== CODE HIGHLIGHTING
****************************************/

  pre, pre code {
    color: #fff;
    background-color: #212936 !important;
  }

  pre code, pre .subst, pre .tag .title, pre .lisp .title, pre .clojure .built_in, pre .nginx .title {
    color: #fff;
  }

/****************************************
  ==== Tooltips (Glossary)
****************************************/

  .tooltip {
    border-bottom: 1px dotted gray;
    color: #00b0ff;
  }

  .tooltip .tooltiptext::after {
    border-color: #ccc;
  }

  .tooltip .tooltiptext {
    background-color: #222c37;
    color: #fff;
  }

/****************************************
  ==== SCROLLBAR STYLE
****************************************/

  #customScrollbar::-webkit-scrollbar-thumb {
    background-color: rgba(109, 67, 175, 0.4);
  }

  #customScrollbar::-webkit-scrollbar-thumb:hover {
    background-color: rgba(109, 67, 175, 0.8);
  }

/****************************************
  ==== Packages
****************************************/

  .navbar-default {
    background-color: #6d43af;
    border-color: #000;
  }

  .sidetoc, .sidefilter, .naw, .toc {
    background-color: #161616 !important;
  }

  .sidetoc, .sidefilter {
    border-width: 2px !important;
    border-color: #fff !important;
  }

  .toc .nav li a {
    color: #00b0ff;
  }

  .toc .nav li a:hover {
    text-decoration: underline;
  }

  .toc .nav li a:hover, .toc .nav>li.active>a, .affix>ul>li.active>a, .affix>ul>li.active>a:before {
    color: #00d71c;
  }

  .affix ul>li>a {
    color: #e4d100;
  }

  .affix ul>li>a:hover {
    color: #fff;
  }

  .toc-filter {
    outline: none !important;
    border-radius: 3px;
    background: #fff;
    color: #e4e4e4 !important;
    padding: 2px;
    margin: 3px;
  }

  .toc-filter>input {
    color: #e4e4e4 !important;
  }

  .component-select__current {
    color: #000;
    background-color: #fff;
  }

  .toc-filter>input {
    color: #000 !important;
  }

  .hljs {
    color: #fff;
  }

  .hljs-keyword {
    color: #ff1281;
  }

  thead {
    background-color: #222c37;
  }

  .hljs-title {
    color: #9556ff !important;
  }

  .hljs-number {
    color: #00c9ff !important;
  }

  .hljs-string {
    color: #ff2424 !important;
  }
}
