css:
@-moz-document domain("dangeru.us") {
body {
    background-image: linear-gradient(to left, #0f0f0f, #191919);
    background-color: #191919;
}

@font-face {
	font-family: 'Luxi Sans';
	src: url('https://cdn.privatevoid.net/fonts/luxi.ttf') format('truetype');
}

@font-face {
	font-family: 'Luxi Mono';
	src: url('https://cdn.privatevoid.net/fonts/mono.ttf') format('truetype');
}

a, .boarda, #newthread, .redtext, .comment,
.comment-styled, #boardtitle, .button_styled,
.special_styled, #thread_watcher {
    font-family: 'Luxi Sans', sans-serif; /* because screw adding font-family under body */
}

#title {
    font-family: 'Luxi Mono', monospace;
    padding-top: 15px;
    text-align: center;
    text-shadow: 0px 0px 7px #4f4f4f;
}

.sticky-1, .sticky-2, .sticky-3 { color: #96a67b; }
.sticky-4, .sticky-5, .sticky-6 { color: #7ba67b; }
.sticky-7 { color: #7ba699; }

#sitecorner {
    background-color: #191919;
    border-color: #343434;
    border-radius: 0px;
    width: 450px;
}

#sitecorner img {
    border: 1px solid #343434;
    padding-bottom: 0px;
}

#maincontainer {
    background-color: #191919;
    border-color: #343434;
    border-top: 9px solid #343434;
    border-bottom: 9px solid #343434;
    border-radius: 0px;
    margin-bottom: 10px;
}

#boardtitle {
    padding-top: 8px;
    text-shadow: 0px 0px 3px #909090;
}

#boardcontainer .boarda {
    margin-top: -9px;
}

#draggable {
    height: auto;
    padding: 0.5em;
    background-color: #191919;
    border: 1px solid white;
    text-align: center;
    color: white;
}

#draggable .comment {
    text-align: left;
    padding-left: 13px;
}

.redtext {
    color: #8abd8a; /* i love fourchong */
    background-color: #252823;
}

#thread_watcher {
    background-image: linear-gradient(to top, #191919, #343434);
    background-color: #111;
    border: 1px solid #343434;
    border-left: 9px solid #343434;
}

#thread_watcher:hover {
    background-image: linear-gradient(to bottom, #282828, #343434);
    cursor: pointer;
}

#thread_watcher_box>div {
    border-top: 0px;
    border-bottom: 0px;
    padding-left: 10px;
    padding-right: 10px;
    padding-top: 3px;
    padding-bottom: 5px;
}

#thread_watcher_box .fa { padding-top: 4px; }

.thread_watcher_new {
    color: #96a67b;
    padding-right: 8px;
}

#hover {
    padding: 4px 8px;
    background-color: #191919;
    border: 2px solid #8abd8a;
}

.selected {
    border: 1px solid #8abd8a;
    border-left: 5px solid #8abd8a;
    padding: 3px 6px 9px;
}

#lockedcontainer {
    border: 1px solid #d75e53;
    background-color: #281e1e;
    border-right: 6px solid #d75e53;
    padding: 4px;
    box-shadow: 4px 5px 4px #0f0f0f;
}

#lockedcontainer .redtext {
    color: #d75e53;
    text-shadow: 1px 1px 3px black;
    background-color: #281e1e;
    font-weight: bold;
    padding-right: 4px;
}

.spoiler {
    color: #0c0c0c;
    background-color: #0c0c0c;
    border-bottom: 1px solid #999;
}

#header {
    padding-bottom: 0px;
    margin-bottom: 9px;
    border: 1px solid #343434;
    border-top: 6px solid #343434;
    border-bottom: 6px solid #343434;
}

#index-news { padding-top: 6px; }

#index-news .redtext {
    color: #9f9f9f;
    background-color: transparent;
    font-style: italic;
    text-shadow: 2px 2px 3px black;
}
}

@-moz-document url("https://dangeru.us/") {
#maincontainer {
    background-image: url('https://cdn.privatevoid.net/img/back_dark.png');
    background-position: right bottom;
    background-repeat: no-repeat;
}

img[src*="logo"] { content: url("https://cdn.privatevoid.net/img/logo_dark.png") }
}
