css:
@-moz-document domain("store.steampowered.com"), domain("steamcommunity.com") {
* {
	font-family: 'Roboto', sans-serif;
}


.profile_item_links {
	margin-bottom: 0!important;
}

/* Profile avatar background */

.profile_header_bg_texture, .profile_header, .profile_header_bg {
    background-color: #23272a;
	background-image: none!important;
	border-radius: 0 0 1rem 1rem;
}

.profile_background_overlay_content {
	background-color: #23272a;
}

.profile_customization {
	background-image: none!important;
	background-color: #23272a;
}

.profile_content.has_profile_background {
	margin-top: 0px;
}

.profile_background_overlay_content {
	opacity: 1;
}

.btn_profile_action, .btn_profile_action > span {
	background: #7289da!important;
	border-radius: 15px;
	border: none!important;
}

.playerAvatar.in-game, .friend_block_holder.friend_status_in-game .friend_block_avatar, .friend_activity.friend_status_in-game .friend_block_avatar, .appHubIconHolder.in-game, .avatar_block_status_in-game {
	background: #7289da;
}

.playerAvatar.profile_header_size .playerAvatarAutoSizeInner img, .profile_header_size.in-game {
	border-radius: 50%;
}

.commentthread_comment_timestamp, .persona, a.persona, .persona a, .friend_status_offline, .friend_status_offline div, .friend_status_offline a {
	color: #99aab5;
}

.profile_header_badge {
    border: 2px solid #7289da;
    border-radius: 6px;
    background-color: #23272a;
    height: 70px;
    box-shadow: none!important;
    margin-bottom: 13px;
}

.recent_game {
	border: 2px solid #7289da;
    border-radius: 6px;
}
}
