css:
@-moz-document url("https://learn.lambdaschool.com/") {
* {
    color: #e6e6e6
}

.docs-sidebar {
    background-color: #000;
}

.content-container {
    background-color: #000;
}

#welcome {
    background-color: #212121;
}
}

@-moz-document url-prefix("https://learn.lambdaschool.com/") {
p {
    color: #fff;
    font-size: 1.1em;
}
ul,
li {
    color: #fff;
}
.content-container {
    background-color: #212121;
}
.jumbotron {
    padding-left: 0;
    background-color: #212121;
    color: #fff;
}
.alert-warning {
    background-color: #212121;
}
.sticky-nav {
    background-color: #14121f;
}
.sprint-nav .section-nav a:active,
.sprint-nav .section-nav a:link,
.sprint-nav .section-nav a:visited {
    color: #fff;
}
.mark,
mark,
.alert-warning,
.alert {
    background-color: #CF6679;
}
dt,
h1,
h2,
h3,
h4,
h5 {
    color: #fff;
    text-align: center;
    text-decoration: underline;
}
.wrapper {
    margin: 0 auto;
    width: 40%;
    min-width: 600px;
}
iframe {
    /* To inherit to all pages with missing inline styling */
    margin: 0 auto;
    display: block;
    width: 560px;
    height: 315px;
}
}
