css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.google.com/search?),
               url-prefix(http://www.google.cz/search?) {
ol .g
{
  margin-left: 13px !important;
  margin-bottom: 20px !important;
}
/* Title - text*/
ol .g .r a, ol .g .ts td a
{
  font-family: Candara !important;
  font-size: 21px !important;
  font-weight: bold !important;
  color: #219bbe !important;
  /*background-color: #1577c2 !important;*/
  text-decoration: none !important;
}
/* Title - translate*/
ol .g .std a, ol .g .std 
{
  color: #777 !important;
}
/* Display as (HTML) */
ol .g .s a 
{
  color: #999 !important;
}
/* Display as (HTML):hover */
ol .g .s a:hover
{
  color: #777 !important;
}
/* File format */
ol .g .s .f
{
  display: none !important;
}
/* Title - counter */
ol .g .r span, ol .g .ts td span
{
  color: #ccc !important;
  font-weight: bold !important;
}
/* Title - file format */
ol .g .xsm
{
  display: none !important;
}

/* Text */
ol .g .s, ol .g .ts td .s
{
  color: #3a3a3a !important;

  line-height: 18px !important;
  margin-top: 5px !important;
}
/* Link */
ol .g .s cite, ol .g .ts td .gl cite
{
  /*color: #679e1e !important;*/
  color: #64A313 !important;
  font-weight: bold !important;

  line-height: 22px !important;
}
/* Archiv etc */
ol .g .s .gl a, ol .g .ts td .gl .fl, ol .g .ts td .gl a 
{
  color: #ccc !important;
}
ol .g .ts td .gl a
{
  font-size: small !important;
}
ol .g .s .gl a:hover
{
  color: #999 !important;
}
ol .g .s .slk a
{
  color: #999 !important;
}
ol .g .s .slk a:hover
{
  color: #777 !important;
}
}
