css:
@-moz-document url-prefix("https://learn.lambdaschool.com/"), url-prefix("https://apply.lambdaschool.com/"), url-prefix("https://challenge.lambdaschool.com/"), url-prefix("https://lambdaschool.com/") {
body{
    background-color:#333;
    color:#fff !important;
}

h1, h2, h3, h4, h5, h6{
    color:#fff !important;
}

div.content-container {
    background-color: #333;
}

h1.entry-title{
    color:#fff !important;
}

h2#welcome {
    color: #fff;
}

p {
    color: #DDD;
}

h3 {
    color: #ccc;
}

.section-nav {
    color: #fff !important;
    border: none;
}

a:visited {
    color: #fff !important;
    opacity: 1 !important;
}

a:link {
    color: #fff !important;
    opacity: 1 !important;
}

a:active {
    color: #fff !important;
    opacity: 1 !important;
}

h1#content.bd-title {
    text-align: center;
}

iframe {
    display: block;
    width: 560px !important;
    height: 315px !important;
    margin: 0 auto !important;
}

div.alert.alert-warning {
    background-color: #666;
    color: #fff;
    border: none;
}

h4.mark.mb-3.p-2 {
    background-color: #666;
    color: #fff;
    border: none;
}

div.header-row {
    background-color: #333;
}

div.docs-sidebar {
    background-color: #222;
    border-right: 5px solid #333 !important;
}

div.sidebar-title {
    color: #CCC;
}

div.wrapper {
    background-color: #333;
    color: #fff;
}

div.jumbotron {
    background-color: #333;
}

div.sticky-nav {
    background-color: #333;
    color: #fff;
}

div.sprint-nav {
    color: #fff !important;
}

main.content-container {
    background-color: #333;
}

div#content.site-content{
    background-color:#333;
}

.ast-article-single{
    background-color:#333 !important;
}

div.ast-footer-overlay{
    background-color:#222;
    border:none;
}

div.main-header-bar{
    background-color:#222;
    border:none;
}

#main.site-main{
    background-color:#333 !important;
}

div.learndash_profile_details{
    color:#fff;
}

div.learndash_profile_heading{
    color:#fff !important;
}

div.entry-content{
    background-color:#444;
    border-radius:5px;
    padding:50px;
}

div.profile_avatar img{
    border-radius:50px !important;
    border: 2px solid #fff !important;
}

.w-nav{
    background-color:#222;
}


.navigation-wrap{
    background-color:#222;
}

.section{
    background-color:#333 !important;
    color:#fff !important;
    border:none;
}

div.headline-text{
    color:#fff !important;
}

strong{
    color:#fff !important;
}


.large-subheadline{
    color:#fff !important;
}

.home{
    color:#000 !important;
}

.body-copy{
    color:#fff !important;
}

.checkmark{
    color:#fff;
}

.dropdown-list{
    background-color:#333;
    border:none;
}

.white-box{
    background-color:#333 !important;
}

.w-slide{
    background-color:#333;
}

}
