css:
@-moz-document url-prefix("https://interox.fr/"), url-prefix("https://clients.interox.fr/") {
/* üëè Bient√¥t je travail pour la NASA. üëè */

.intro {
    background: linear-gradient(to bottom, #c05b5b, #933939);
}
.main-nav {
    background: linear-gradient(to right, #c05b5b, #c05b5b);
    overflow: hidden;
    border-bottom: solid 2px white;
}
.theme-background-color {
    background-color: #813232;
}
.product-1, .product-2, .product-3 {
    background: linear-gradient(to bottom, #fff, #cdcdcd91);
    border: solid 2px #ba5656a1;
    -webkit-box-shadow: 10px 7px 36px -6px rgba(0, 0, 0, 0.1);
    -moz-box-shadow: 10px 7px 36px -6px rgba(0, 0, 0, 0.1);
    box-shadow: 0 -1px 8px 2px rgba(0, 0, 0, 0.18);
}
.product-1:hover, .product-2:hover, .product-3:hover {
    background: linear-gradient(to bottom, #fff, #f0f4f7);
    border: solid 2px #ba5656a1;
    -webkit-box-shadow: 10px 7px 36px -6px rgba(0, 0, 0, 0.1);
    -moz-box-shadow: 10px 7px 36px -6px rgba(0, 0, 0, 0.1);
    box-shadow: 0 -1px 8px 2px rgba(0, 0, 0, 0.18);
}
.intro-button-right {
    width: 150px;
    border: 2px solid #fffcf1;
    background-color: #933939;
}
.intro-button-right:hover {
    width: 150px;
    border: 2px solid #933939;
    box-shadow: 0 0 4px 1px #00000052;
    background-color: #fffcf1;
}

.intro-button-left {
    width: 150px;
    border: 2px solid #fffcf1;
    background-color: #723636;
}
.intro-button-left:hover {
    width: 150px;
    box-shadow: 0 0 4px 1px #00000052;
    border: 2px solid #723636;
    background-color: #fffcf1;
}
.intro-button-left p:hover {
    color: #723636
}

.intro-button-right p:hover {
    color: #933939;
    
}

.price {
    font-size: 30px;
    color: #a24444;
}
.product-top {
    color: #890000;
}

.button-footer {
    border: 3px solid #fffcf1;
    border-radius: 4px;
    background-color: #933939;
}
.button-footer:hover {
	border: 2px solid #933939;
    border-radius: 4px;
    box-shadow: 0 0 4px 1px #00000052;
    background-color: #fffcf1
}

.fa-flag {
    color: #fffcf1;
}
.button-footer p:hover {
    color: #933939;
}
.button-footer fa-flag:hover {
    color: #933939;
}
.footer-top {
    overflow: hidden;
    background: linear-gradient(to right, #c05b5b, #933939);
}
.rating {
    background-color: #813232;
    overflow: hidden;
    color: #fff;
}
.footer ul li a:hover {
    text-decoration: underline;
    color: #fff;
}

/* Partie clients */

.navbar-main {
    margin-bottom: 0;
    background-color: #770e0e;
}
ul.top-nav>li.primary-action>a.btn {
    padding: 6px 15px;
    background-color: #770e0e;
}
.form-control:focus {
    border-color: #e96666;
    outline: 0;
    box-shadow: inset 0 1px 1px rgba(0,0,0,.075), 0 0 8px rgba(233, 102, 102, 0.6);
}
div.header-lined h1 {
    margin-top: 0;
    margin-bottom: 15px;
    padding: 6px 0;
    border-bottom: 1px solid #ccc;
    font-weight: 400;
    color: #800;
}
.btn-primary {
    color: #fff;
    background-color: #b73333;
    border-color: #a42e2e;
}
.btn-primary:focus {
    color: #fff;
    background-color: #902828;
    border-color: #401212;
}
.btn-primary:hover {
    color: #fff;
    background-color: #902828;
    border-color: #742020;
}
.btn-primary:active {
    color: #fff;
    background-color: #902828;
    border-color: #742b2b;
}
.btn-primary.active.focus, .btn-primary.active:focus, .btn-primary.active:hover, .btn-primary:active.focus, .btn-primary:active:focus, .btn-primary:active:hover, .open>.dropdown-toggle.btn-primary.focus, .open>.dropdown-toggle.btn-primary:focus, .open>.dropdown-toggle.btn-primary:hover {
    color: #fff;
    background-color: #742020;
    border-color: #401212;
}
.alert-info {
    color: #8f3131;
    background-color: #f7d9d9;
    border-color: #f1bcbc;
}
.navbar-main li.account {
    background-color: #5f0404;
}
.navbar-main li.account:hover {
    background-color: #5f0404;
}

element.style {
}
.navbar-main .navbar-nav>.active>a, .navbar-main .navbar-nav>.active>a:focus, .navbar-main .navbar-nav>.active>a:hover, .navbar-main .navbar-nav>.open>a, .navbar-main .navbar-nav>.open>a:focus, .navbar-main .navbar-nav>.open>a:hover, .navbar-main .navbar-nav>li>a:focus, .navbar-main .navbar-nav>li>a:hover {
    color: #eee;
    background-color: #5f0404;
}
.btn-link {
    font-weight: 400;
    color: #b73333;
}
.btn-link:focus, .btn-link:hover {
    color: #7c2323;
}
a {
    color: #800;
}
.tile a:hover {
    color: #b73333
}
.main-nav ul li a:hover {
    color: #723636;
    text-shadow: 0px 0px 2px #ffffff26;
}
.ribbon span {
    position: absolute;
    display: block;
    width: 225px;
    padding: 15px 0;
    background-color: #813232;
}
.ribbon::before, .ribbon::after {
    position: absolute;
    z-index: -1;
    content: '';
    display: block;
    border: 5px solid #813232;
}
.feature-plesk {
    overflow: hidden;
    padding-top: 30px;
    padding-bottom: 30px;
    background-color: #e65353;
    color: white;
}
.plesk-button {
    padding: 10px 15px 10px 15px;
    width: 110px;
    border: 1px solid #d93434;
    margin-left: 40px;
    border-radius: 4px;
    background-color: #e23636;
}
element.style {
    display: inline-block;
    background-color: #813232;
    border-color: #ffffff;
    margin-top: 60px;
    margin-bottom: 0;
}
element.style:hover {
    display: inline-block;
    background-color: #813232;
    border-color: #813232;
    margin-top: 60px;
    margin-bottom: 0;
}
.techincal {
    overflow: hidden;
    padding-top: 130px;
    padding-bottom: 130px;
    background-color: #352323;
    display: none;
    color: #fff;
}
.techincal .line {
    height: 2px;
    width: 50px;
    background-color: #813232;
    margin-top: -10px;
}
/* .theme-background-color .theme-text-color {
    background-color: #813232 !important;
} */
}
