css:
@-moz-document domain("anitokyo.tv") {
body {
    background: #101010!important;
}
#wrapper {
    width:85%!important;
    position: relative;
}
header {
    clear: both;
    height: 203px;
    margin: 0;
    position: relative;
    z-index: 5;
    padding: 230px 0 0 0;
    background: #101010!important;
}
/**/

/**/
/* ВЫДЕЛЕНИЕ */
a, .r_title a, 
.lastcomm .rcols, 
.static_h, 
.statistic .inf_stat li b,  
.auth label span, 
.login_block_h,
.matinee, 
.story h1.story_h,
.story_inf ul.lcol li b, 
.story .story_c a, 
.story_inf ul.lcol li a
aside.rcol .block .block_h,
.ul_inf li {
    color: #bb3!important; /* 48f :: 3b3 */
}
/* ВЫДЕЛЕНИЕ ФОН */
.statistic .inf_stat li span, 
.statistic .tit, 
.tabs li.current,
.RalodePlayer .serie-active {
    background: #bb3!important;
    color: #000!important;
}
/* под мышкой */
.RalodePlayer .RlItem:hover:not(.serie-active),
.lastcomm:hover,
.RalodePlayer .buttonLR:hover
{
    background: #ee3!important;
    color: #000!important;
}
/**/

/* ЦВЕТ ТЁМНЫЙ 0 */
.related li a:hover,
aside.rcol .block .block_h,
.story .story_inf,
.RalodePlayer .RlItem,
.RalodePlayer .buttonLR,
aside.lcol .block .block_h,
.RalodePlayer .buttonLR,
.ul_inf li.add {
    color: #000;
}
/* ЦВЕТ СВЕЛЫЙ 1 */
#contents,
.section, 
section.lcol,
ul.tabs,
.box,
.statistic,
aside.rcol .block .block_c{
    color: #888;
}
/* ЦВЕТ СВЕЛЫЙ 2 */
.berror_c,
aside.lcol .block .block_h,
#dle-reviews-list .good,
h1,
h2,
h3,
h4,
h5,
h6 {
    color: #aaa;
}
/* ЦВЕТ СВЕЛЫЙ 3 */
.tabs li,
.story .story_b,
.static_h,
.lastcomm,
.comment,
.comment:last-child,
.speedbar,
aside.rcol .block,
aside.lcol .block,
aside.rcol .block .block_c,
.lastcomm,
.story_inf ul.lcol li {
    color: #ccc;
}
/* ЦВЕТ СВЕЛЫЙ 4 */
.ratingzero,
.RalodePlayer .buttonLR:hover,
.related li a,
.comment_inf {
    color: #fff;
}

/**/
/* ОСОБОЕ ВЫДЕЛЕНИЕ */
.related li a,
.comment_inf {
    background: #440;
}
/* под мышкой */
.related li a:hover
{
    background: #884;
}
/**/
/* КНОПКА ВОЙТИ */
.auth button[type="submit"]{
    background: #4c4!important;
}
.auth button[type="submit"]:hover {
	background: #3b3!important;
}
/**/
/* ОБЫЧНЫЙ ТЕКСТ */
.lastcomm .rcols a, 
.story .story_c,
.comment_text {
    color: #888!important;
}

/**/
/* ЦИФРЫ РЕЙТИНГА */
.ratingzero {
    font-weight: bold;
}
.ratingplus {
	color: #0f0!important;
    font-weight: bold;
}
.ratingminus {
    color: #f00!important;
    font-weight: bold;
}

/**/
/* ТЁМНЫЙ ФОН */
#contents {
	background: #111;
    background-color: #111;
}

/* СРЕДНИЙ ФОН */
.section, 
section.lcol,
ul.tabs,
.box,
.statistic,
aside.rcol .block .block_c
{
    background: #252525;
	background-color: #252525;
}

/* СВЕТЛЫЙ ФОН */
.tabs li,
.story .story_b,
.static_h,
.lastcomm,
.comment,
.comment:last-child,
.speedbar,
aside.rcol .block,
aside.lcol .block,
aside.rcol .block .block_c {
    background: #333;
    background-color: #333;
}

/* ЯРКИЙ ФОН */
aside.rcol .block .block_h,
.story .story_inf,
.RalodePlayer .RlItem,
.RalodePlayer .buttonLR,
aside.lcol .block .block_h,
.RalodePlayer .buttonLR {
	background: #555;
    background-color: #555;
}
/* под мышкой */
.RalodePlayer .buttonLR:hover {
    background: #888;
    background-color: #888;
}



/**/
/* ТАБЛИЧКА С ОШИБКОЙ */
.berror {
    background: #a22;
}
.berror_c {
    background: #a22;
}

/**/
/* АВАТАРЫ ТАБЛИЦЕЙ СЛЕВА */
.whoonline-foto,
a.online_user img {
    width: 0px;
    height: 0px;
}
/**/

.section {
    width: 100%;
    margin: 0 0 30px;
    clear: both;
    margin-top: 10px;
}
section.lcol {
    width: 90%;
}
aside.rcol {
    width: 10%;
}
.content {
    width: auto;
    overflow: hidden !important;
}

/**/
.statistic .inf_stat li {
    color: #6b7982;
    background: #222;
}

/**/
/* ОБЗОРЫ */
 #dle-reviews-list .good {
    background: #343;
}
 #dle-reviews-list .bad {
    background: #433;
}

.scriptcode,
.title_quote, 
.title_spoiler, 
.text_spoiler {
    background: #433;
}
.ul_inf li.add {
    font-weight: bold;
}
}
