css:
@-moz-document domain("raidloot.com") {
#widecontent {
    background: #262626;
    color: #808080;
}
#body {
    background: #262626;
    color: #808080;
}
input, select {
    background: #808080;
    border-style: none;
    color: #000;
}
option {
    background: #808080;
}
label {
    color: #808080;
}
h1 {
    color: #808080;
}
span {
    color: #808080;
}
.item {
    color: #808080;
}
.mana {
    color: lightblue;
}
th {
    background: #262626;
    color: #fff;
    border: solid 1px #808080;
}
td {
    color: #808080;
    border: solid 1px #808080;
}
.tip {
    background: #808080;
}
.tip label {
    color: #262626;
}
.tip a {
    color: #262626;
}
li {
    color: #262626;
}
a {
    color: lightblue;
}
.MainContent_SearchText {
    background: #262626 !important;
}
.tip p, li {
    color: #000;
}
.tip strong {
    color: #000;
}
.tip .note {
    color: #000;
}
.comment {
    background: #262626;
}
.ac {
    color: lightgreen;
}
.score {
    background: #808080;
    color: #000;
}
}
