css:
@-moz-document url-prefix("https://vault.bitwarden.com/"), url-prefix("https://bitwarden.wmx.me") {
body {
 	background: #121212;   
 	background-color: #121212 !important;   
    color: #fff;
}

a {
 	color: #fff;   
}

a:hover {
 	color: #fff;   
}

.bg-primary, .modal-footer {
    background-color: #202020 !important;
    border-radius: 0px;
}

.table {
 	color: #fff;   
}

.table th, .table td, .page-header, .secondary-header, .footer,
.dropdown-menu .dropdown-divider {
	border-color: rgba(255,255,255,.1);   
}

.footer,
.text-muted, .card-header small, .modal-header small,
app-vault-groupings .card h3, app-org-vault-groupings .card h3,
.table.table-list td small, .table.table-list td > .fa, .table.table-list td .icon {
 	color: rgba(255,255,255,.4) !important;   
}

.bg-light {
 	background: #000 !important;   
}
}

@-moz-document url-prefix("https://vault.bitwarden.com/"), url-prefix("https://bitwarden.wmx.me") {
.card,
.dropdown-menu,
.modal-content {
 	background: #1d1d1d;   
    color: #fff;
}

.dropdown-menu .dropdown-item,
.dropdown-item-text {
 	color: #fff;   
}

.card-header { 
 	border-bottom: none;
    background: none;
}

app-vault-groupings .card .card-body a, 
app-org-vault-groupings .card .card-body a,
.text-body {
 	color: #ffffff !important;   
}
}

@-moz-document url-prefix("https://vault.bitwarden.com/"), url-prefix("https://bitwarden.wmx.me") {
.btn[class*="btn-outline-"]:not(:hover), 
.swal-modal .swal-footer button.swal-button[class*="btn-outline-"]:not(:hover),
.form-control,
.form-control:focus {
 	background: #000 !important;
    border-color: transparent !important;
    color: #fff;
}

.btn-outline-secondary:not(:disabled):not(.disabled):active, .swal-modal .swal-footer button.swal-button.swal-button--cancel:not(:disabled):not(.disabled):active, .btn-outline-secondary:not(:disabled):not(.disabled).active, .swal-modal .swal-footer button.swal-button.swal-button--cancel:not(:disabled):not(.disabled).active, .show > .btn-outline-secondary.dropdown-toggle, .swal-modal .swal-footer .show > button.dropdown-toggle.swal-button.swal-button--cancel {
 	color: rgba(255,255,255, .75);   
}

.modal-header, .modal-footer {
 	border-color: rgba(255,255,255, .1);   
}

.modal .close {
 	text-shadow: none;
    color: #fff;
}

.btn-outline-secondary:not(:disabled):not(.disabled):active, .swal-modal .swal-footer button.swal-button.swal-button--cancel:not(:disabled):not(.disabled):active, .btn-outline-secondary:not(:disabled):not(.disabled).active, .swal-modal .swal-footer button.swal-button.swal-button--cancel:not(:disabled):not(.disabled).active, .show > .btn-outline-secondary.dropdown-toggle, .swal-modal .swal-footer .show > button.dropdown-toggle.swal-button.swal-button--cancel {
 	background: #1d1d1d;
    border: none;
}
.list-group-item,
.callout,
.progress,
.nav-tabs .nav-link.active, .nav-tabs .nav-item.show .nav-link {
 	background: #1e1e1e;
}

.org-nav .nav-tabs a {
 	color: #fff;   
}

.nav-tabs .nav-link.active, .nav-tabs .nav-item.show .nav-link {
    border-color: #1d1d1d;
    color: #fff;
}

.org-nav {
    background: #000;
    border-color: #1d1d1d;
}

.list-group-item.active {
 	background: #000;
    color: #fff;
}
}
