.main-nav { 
    background: none;
    margin-top: 40px;
}
.product-1:hover, .product-2:hover, .product-3:hover {
    transition: all 0.5s;
    box-shadow: 0 0 10px grey;
    transform: translateY(-2%)
                                            
}
.intro {
    background: none;
}
.rating {
    z-index: 1;
    bottom: 0;
    position: fixed;
    width: 100%
}
.bottom-nav {
    z-index: 1;
    position: fixed;
    top: 0;
    width: 100%;
}
.rating {
  background:  #813232;
}
.rating h3 img {
    background-color: #b60000;
}

.intro-button-left {
    width: 150px;
    border: 3px solid #813232;
    border-radius: 4px;
    background-color: #813232;
}

.intro-button-left:hover {
    width: 150px;
    border: 3px solid #813232;
    border-radius: 4px;
    background-color: #813232;
}

.intro-button-left {
    width: 150px;
    border: 3px solid #ac3a3a;
    border-radius: 4px;
    background-color: #ac3a3a;
}

.intro-button-right {
    width: 150px;
    border: 3px solid #933939;
    border-radius: 4px;
    background-color: #933939;
}
.intro-button-right:hover {
    width: 150px;
    border: 3px solid #dd4a4a;
    border-radius: 4px;
    background-color: #dd4a4a;
}
.product-1, .product-2, .product-3 {
    background: linear-gradient(to top, #492525, #661212);
    border: 1px solid #562c2c;
    z-index: 0;
}
.price {
    font-size: 30px;
    color: #e1acac;
}
.footer-top {
    overflow: hidden;
    background: linear-gradient(to left, #c05b5b, #933939);
}
.button-footer {
    border: 3px solid #813232;
    border-radius: 4px;
    background-color: #813232;
}
.button-footer:hover {
    border: 3px solid #9e2b2b;
    border-radius: 4px;
    background-color: #9e2b2b;
}
.footer-title {
    font-weight: 600;
    font-family: Montserrat;
    text-transform: uppercase;
    color: #bc5858;
    margin-bottom: 5px;
}
.element.style {
    color: #b60000;
}
.product-top {
    color: white;
}
.product-main p {
    color: #dac0c0;
    text-align: center;
}
body {
    margin: 0;
    padding: 0;
    background-color: #1f2d3d;
    color: #dac0c0;
}
.footer-bottom {
    background: none;
    overflow: hidden;
    color: #e6d3d3;
    margin-bottom:50px;
}
.main-nav ul li a:hover {
    color: #621414;
    text-decoration: none;
    text-transform: uppercase;
    font-size: 14px;
    margin-left: 15px;
    font-family: Montserrat;
    font-weight: 500;
    animation-duration: 2s;
    text-shadow: -1px 0px 7px #ffffff52;
}
.footer {
    overflow: hidden;
    background: #3d1f1f;
    color: #f6e3e3;
    font-size: 16px;
    padding-top: 125px;
    padding-bottom: 125px;
}
.bottom-nav {
    background-color: #2a2a2a;
    overflow: hidden;
}
.ribbon span {
    position: absolute;
    display: block;
    width: 225px;
    padding: 15px 0;
    background-color: #813232;
}
.ribbon::before, .ribbon::after {
    position: absolute;
    z-index: -1;
    content: '';
    display: block;
    border: 5px solid #813232;
}
element.style {
    display: inline-block;
    background-color: #813232;
    border-color: #813232;
    margin-top: 60px;
    margin-bottom: 0;
}
.questions-new .line {
    height: 2px;
    width: 50px;
    background-color: #813232;
    margin-top: -10px;
}
}
