css:
@-moz-document domain("www.udemy.com") {
body {
    background-color: black;
}
div[class^="app--sidebar-column"],
div[class^="sidebar--sidebar"] {
    background-color: black;
    color: white !important;
    border: none;
}

div[class^="app--sidebar-column"] span {
    color: #dddddd;
}

div[class^="sidebar--header"],
div[class^="sidebar--content---4z0-"] {
    background-color: #191d24;
    color: white !important;
    border-color: #313947;
}

div[class^="section--section"] {
    background-color: #29303b;
    border-color: #313947;
}

div[data-purpose="section-heading"] {
    background-color: #191d24 !important;
    border-color: #313947;
}

li[class*="curriculum-item-link--is-current"] > div {
    background-color: #495669 !important;
}

li[class*="curriculum-item-link"] > div:hover {
    background-color: #191d24 !important;
}

button[aria-label="Resource list"] {
    background-color: #191d24;
    border-color: #313947;
}

div[class^="app--row-content"],
div[class^="question-details--details"] {
    background-color: #111418 !important;
}

div[class^="app--dashboard-content"] {
    color: #ddd !important;
}

div[class^="view-more-container--view-more__button-container--gradient"] {
    background: linear-gradient(rgba(0, 0, 0, 0), rgba(0, 0, 0, 0.95), #000)
}

li[role="presentation"] a,
div[class^="question-list-question--title"],
div[data-purpose="question-body"] {
    color: #bbb;
}

li[role="presentation"][class*="active"] a {
    color: #eee !important;
    font-weight: bold
}

div[class^="dashboard--content"],
div[class^="course-overview--grid-row"],
div[class^="question-list-question--question-wrapper"] {
    border-color: #313947;
}

div[class^="question-overview--search-bar"] {
    background-color: #313947;
}

div[class^="question-list-question--question-wrapper"]:hover {
    background-color: #29303b;
}

* img {
    filter: brightness(0.6);
}

* img:hover {
    filter: brightness(0.8);
}

input,
span[class*="input-group-btn"] button {
    background-color: #515f75 !important;
    border-color: #29303b !important;
}

::placeholder {
    color: #bbb !important;
}

div[class^="footer-v6"] {
    display: none;
}


button[id="lecture-filter"],
button[id="sort-by"],
button[id="filter-by"] {
    background-color: #29303b;
    border-color: #515f75;
    color: #bbb;
}

button[id="lecture-filter"]:hover,
button[id="sort-by"]:hover,
button[id="filter-by"]:hover,
button[aria-expanded="true"],
ul[class="dropdown-menu"] {
    background-color: #313947 !important;
    border-color: #515f75 !important;
    color: #ccc !important;
}

code {
    background-color: #111418;
    border-color: #191d24;
}
}

@-moz-document domain("udemy.com") {
/* quiz */

div[class^="quiz-page-layout--quiz-header"],
div[class^="quiz-page-layout--scroll-container"],
footer[class^="curriculum-item-footer--footer"]{
    background-color: #313947 !important;
}

div[class^="quiz-page-layout--quiz-header"],
div[class^="quiz-page-layout--scroll-container"],
footer[class^="curriculum-item-footer--footer"]{
    color: #bbb !important;
}

button[data-purpose="next-question-button"],
button[data-purpose="next-question-button"]:disabled
{
    background-color: #515f75 !important;
}

div[class^="mc-quiz-answer--answer-body"]:hover,
div[class^="mc-quiz-answer--answer-body"] > label:hover
{
    background-color: #515f75 !important;
}

li[class^="question-list--question-summary"] > a {
    color: #aaa !important;
}
header[class^="results-page--header"] {
    opacity: 0.7;
}
}
