css:
@-moz-document domain("dangeru.us") {
body {
    background: #eef2ff url(https://cdn.privatevoid.net/img/fade.png) top center repeat-x;
    color: #000;
}

a, .boarda, #newthread, .redtext, .comment,
.comment-styled, #boardtitle, .button_styled,
.special_styled, #thread_watcher {
    font-family: 'Arial', 'Helvetica', sans-serif;
    font-size: 10pt;
    color: #000;
}

.jsunhide, .button_styled {
    background-image: linear-gradient(to top, #ccc, #eef2ff);
    background-color: #eef2ff;
    color: black;
    border: 1px solid #9a9eb7;
}

#title {
    font-family: 'Arial', 'Helvetica', sans-serif;
    font-size: 18px;
    font-weight: bold;
    color: #117743;
    padding-top: 15px;
    text-align: left;
}

div.redtext {
    color: purple;
    font-weight: bold;
}

div.redtext:after {
    content: url(https://cdn.privatevoid.net/img/modicon.gif);
    margin-left: 6px;
}
/*  in a perfect world, IDs referring to admins and janitors exist
    but this is not a perfect world */

#newthread {
    color: #34345c;
    font-size: 22px;
    font-weight: bold;
    border-top: 1px solid #b7c5d9;
    margin-top: 6px;
    padding-top: 3px;
}

#newthread:before { content: "["; }
#newthread:after { content: "]" }
#newthread:before, #newthread:after { color: #000; }

#newthread:hover {
    color: red;
    text-decoration: none;
    cursor: pointer;
}

.sticky-1, .sticky-2, .sticky-3,
.sticky-4, .sticky-5, .sticky-6,
.sticky-7, .sticky-8, .sticky-9 { color: #117743; font-weight: bold; }

.sticky-1:after, .sticky-2:after,
.sticky-3:after, .sticky-4:after,
.sticky-5:after, .sticky-6:after,
.sticky-7:after, .sticky-8:after,
.sticky-9:after {
    content: url('https://cdn.privatevoid.net/img/sticky.gif');
    padding-left: 3px;
}

#sitecorner {
    background: transparent;
    border: 0px solid #eef2ff;
    border-radius: 0px;
    width: 450px;
}

#sitecorner img {
    border: 1px solid #B7C5D9;
    padding-bottom: 0px;
}

.comment {
    background-color: #D6DAF0;
    border: 1px solid #B7C5D9;
    padding: 4px;
}

footer.comment {
    background-color: transparent;
    border: 0px solid white;
}

#index-news .comment { /* haha i love overlapping */
    background-color: transparent;
    border: 0px solid transparent;
    padding: 0px;
}

#maincontainer {
    width: 450px;
    background-color: #eef2ff;
    border: 1px solid black;
    border-radius: 0px;
    margin-bottom: 10px;
}

#boardtitle {
    padding-top: 8px;
    color: #af0a0f;
    font-family: "Tahoma", sans-serif;
    font-size: 28px;
    font-weight: 700;
    letter-spacing: -2px;
}

#draggable {
    padding: 0.6em;
    background-color: #EEF2FF;
    border: 1px solid black;
    color: white;
    text-align: center;
}

#draggable .comment {
    background-color: transparent;
    border: 0px solid white;
    width: 97%; /* beats having to use padding, sorta fixes the text / inputbox bug */
    text-align: left;
    padding: 0px;
    margin-top: 9px;
}

#draggable #comment {
    width: 297px;
    margin-bottom: 10px;
    font-family: 'Arial', 'Helvetica', sans-serif;
    font-size: 10pt;
}

.autogenerated-link, .boarda {
    font-size: 10pt;
    color: #34345c;
}

.autogenerated-link:hover, .boarda:hover {
    color: red;
    text-decoration: none;
}

#thread_watcher {
    background-image: linear-gradient(to top, #d6daf2, #eef2ff);
    background-color: #d6daf2;
    border: 1px solid #000;
}

#thread_watcher:hover {
    background-image: linear-gradient(to bottom, #d6daf2, #eef2ff);
    cursor: pointer;
    border: 1px solid #aaa;
}

#thread_watcher_box>div {
    border-top: 0px;
    border-bottom: 0px;
    padding-left: 10px;
    padding-right: 10px;
    padding-top: 3px;
    padding-bottom: 5px;
}

#thread_watcher_box .fa {
    padding-top: 1px;
    color: black;
}

.thread_watcher_new {
    color: #789922;
    padding-right: 8px;
}

#wordcount {
    border: 0px solid white;
    background: transparent;
    padding-bottom: 8px;
    padding-top: 6px;
}

#reply_box {
    font-family: 'Arial', 'Helvetica', sans-serif;
    font-size: 11pt;
}

#hover {
    padding: 4px 8px;
    background-color: #D6DAF0;
    border: 1px solid #B7C5D9;
}

.selected {
    background-color: #d6bad0;
    border: 1px solid #ba9dbf;
    padding: 3px 6px 9px;
}

#lockedcontainer .redtext, div.title {
    font-family: 'Arial', 'Helvetica', sans-serif;
    font-size: x-large;
    color: red;
    font-weight: 700;
}

#lockedcontainer .redtext:after {
        content: "";
        content: none;
}
 
.spoiler {
    color: #000;
    background-color: #000;
    border: 0px solid #000;
}

#header {
    padding-bottom: 0px;
    margin-bottom: 9px;
    border: 1px solid #343434;
}

#index-news { padding-top: 6px; }

#index-news .redtext {
    color: #2a2a2a;
    font-weight: bold;
    font-style: italic;
}

textarea,#draggable>form>input[type=text],.text_input {
    resize: none;
    background-color: #eef2ff;
    color: #000;
    border: 1px solid black;
    border-radius: 0px;
    padding: 4px;
    margin-top: 6px;
}

textarea:focus {
    border: 1px solid #ea8;
}

div.title:first-of-type { margin-top: 16px; }
.post_number .redtext { color: #34345c;  }
.post_number .redtext:hover { color: red; }
.redtext { color: #789922; }
#boardcontainer .boarda { margin-top: -9px; }
hr { border-style: none; }
}

@-moz-document url("https://dangeru.us/") {
#maincontainer {
    background-image: url('https://cdn.privatevoid.net/img/yotsuba.png');
    background-position: right bottom;
    background-repeat: no-repeat;
}

img[src*="logo"] { content: url("https://cdn.privatevoid.net/img/logo_y.png") }

.index-links > a {
    font-size: 10pt;
    text-decoration: underline;
    border-bottom: 0px solid black;
}

.index-links > a:hover {
    text-decoration: underline;
    color: red;
}

#header { border: 0px solid white; }
}

@-moz-document url-prefix("https://dangeru.us/d") {
body {
    background: #ffe url(https://cdn.privatevoid.net/img/fade_y.png) top center repeat-x;
    color: maroon;
}

.sticky-1, .sticky-2, .sticky-3,
.sticky-4, .sticky-5, .sticky-6,
.sticky-7 { color: red; font-weight: bold; }

#hover {
    padding: 4px 8px;
    background-color: #F0E0D6;
    border: 1px solid #D9BFB7;
}

.autogenerated-link, .boarda {
    font-size: 10pt;
    color: maroon;
}

.comment {
    background-color: #F0E0D6;
    border: 1px solid #D9BFB7;
}

.selected {
    background-color: #f0c0b0;
    border: 1px solid #d99f91;
    padding: 3px 6px 9px;
}

#thread_watcher {
    background-image: linear-gradient(to top, #fed6af, #ffffee, #ffffee);
    background-color: #d6daf2;
}

#thread_watcher:hover { background-image: linear-gradient(to bottom, #fed6af, #ffffee, #ffffee); }
/*  reader note: three colors for the sake of making the #ffe more dominant...
    ...and to make the whole thing look less orange-y*/

#thread_watcher_box>div {
    border-top: 0px;
    border-bottom: 0px;
    padding-left: 10px;
    padding-right: 10px;
    padding-top: 3px;
    padding-bottom: 5px;
}

#thread_watcher_box .fa {
    padding-top: 1px;
    color: black;
}

.thread_watcher_new {
    color: red;
    padding-right: 8px;
}

#title { color: red; }
}
